body {
background: #101010 url(http://i.imgur.com/Wy9QS18.jpg);
}
 
.pokemon {
position: relative;
display: inline-block;
width: 50%;
height: calc(100% / 3);
text-align: center;
border-top: 1px solid white;
border-right: 1px solid white;
background: none;
}
 
#leftpanel {
position: relative;
float: left;
left: 0%;
width: 60%;
height: 540px;
background: -webkit-linear-gradient(top, rgba(16,16,16,1) 0%, rgba(16,16,16,1) 9%, rgba(59,59,109,1) 80%, rgba(64,64,134,1) 100%);
}
 
#rightpanel {
position: relative;
float: right;
width: 40%;
bottom: 0px;
right: 0%;
height: 540px;
background: -webkit-linear-gradient(top, rgba(16,16,16,1) 0%, rgba(16,16,16,1) 9%, rgba(59,59,109,1) 80%, rgba(64,64,134,1) 100%);
}
 
#footerbar {
float: right;
position: relative;
bottom: 0px;
left: 0%;
width: 40%;
height: 60px;
background-color: #404086;
clear: right;
border-top: 1px solid white;
}
 
#randombar {
float: left;
position: relative;
left: 0%;
width: 60%;
height: 60px;
background-color: #404086;
clear: left;
border-right: 1px solid white;
border-top: 1px solid white;
}
 
#page {
margin-top: 1%;
position: absolute;
width: 80%;
margin-left: 10%;
margin-right: 10%;
height: 600px;
float: center;
border-left: 1px solid #efeefe;
border-right: 1px solid #efeefe;
border-bottom: 1px solid#efeefe;
overflow: hidden;
white-space: nowrap;
min-width: 1090px;
max-width: 1090px;
}
 
p {
color: #efeefe;
text-shadow: 2px 2px 0 black;
}
 
button {
font-family: "BankGothic Md BT";
border-radius: 9px;
box-shadow: inset 1px 1px 10px;
}
 
#submitbuttonzone input {
font-family: "BankGothic Md BT";
border-radius: 9px;
box-shadow: inset 1px 1px 10px;
}
 
div#randomtypezone {
background: #101010;
border-radius: 20px;
box-shadow: inset 1px 1px 25px rgba(239, 238, 254, 0.75);
border: 2px solid black;
}
 
div#randomregionzone {
background: #101010;
border-radius: 20px;
box-shadow: inset 1px 1px 25px rgba(239, 238, 254, 0.75);
border: 2px solid black;
}
 
div#randomtierzone {
background: #101010;
border-radius: 20px;
box-shadow: inset 1px 1px 25px rgba(239, 238, 254, 0.75);
border: 2px solid black;
}
 
div#randombuttonzone {
background: #101010;
border-radius: 20px;
box-shadow: inset 1px 1px 25px rgba(239, 238, 254, 0.75);
border: 2px solid black;
}
 
div#randomviablezone {
background: #101010;
border-radius: 20px;
box-shadow: inset 1px 1px 25px rgba(239, 238, 254, 0.75);
border: 2px solid black;
}
 
#exportbox {
border: 2px solid black;
border-radius: 20px;
box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.5);
position: relative;
width: 50%;
height: 80%;
right: 35%;
vertical-align: middle;
text-align: center;
display: inline-block;
visibility: hidden;
min-width: 350px;
bottom: 1%;
background: -webkit-linear-gradient(-45deg, rgba(16,16,16,1) 0%, rgba(59,59,109,1) 48%, rgba(64,64,134,1) 100%);
}
 
#submitteambox {
border: 2px solid black;
border-radius: 20px;
box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.5);
background: -webkit-linear-gradient(-45deg, rgba(16,16,16,1) 0%, rgba(59,59,109,1) 48%, rgba(64,64,134,1) 100%);
}
 
#statzone {
height: 35%;
width: 100%;
border-top: 1px solid white;
}
 
#presetbox {
position: relative;
width: 50%;
height: 90%;
right: 35%;
vertical-align: middle;
text-align: center;
display: inline-block;
background-color: white;
visibility: hidden;
min-width: 350px;
bottom: 1%;
border: 2px solid black;
border-radius: 20px;
box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.5);
background: -webkit-linear-gradient(-45deg, rgba(16,16,16,1) 0%, rgba(59,59,109,1) 48%, rgba(64,64,134,1) 100%);
}
 
#strongzonedivider input {
width: 100%;
text-align: center;
border: 0px;
background: none;
text-shadow: 1px 1px 0px rgba(255, 255, 255, 0.2);
}
 
select {
font-family: Rockwell, "Courier Bold", Courier, Georgia, Times, "Times New Roman", serif;
border-radius: 9px;
box-shadow: inset 1px 1px 10px;
}
 
#statzoneright button {
font-size: 75%;
display: block;
margin-left: auto;
margin-right: auto;
margin-top: 5%;
width: 85%;
font-family: "BankGothic Md BT";
border-radius: 9px;
box-shadow: inset 1px 1px 10px;
}
 
.tablelabel {
width: 40px;
min-width: 40px;
font-family: BankGothic Md BT;
font-size: 70%;
vertical-align: middle;
border-right: 1px solid black;
color: white;
}
 
#statzoneleft, #statzoneright {
display: inline-block;
height: 70%;
width: 50%;
border-top: 1px solid white;
}
 
#statzoneright {
float: right;
border-left: 1px solid white;
}
 
.rightzone {
display: block;
position: relative;
width: 100%;
height: calc(100%/5);
border-top: 1px solid white;
overflow: hidden;
white-space: nowrap;
}
 
