@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("timesofpol.com") {
  body, .post-content, .content-wrap, .menu-main, .menu-main ul, .mobile-menu .sub-menu a, .menu-wrap, .slider-pagination a, .slide-btn, .slider .title, .slider .com, .container, .search-wrap, .menu-wrap, .ac-ad-title-300px:before, .post-template-1 .details .post-small-button, #wp-calendar, .menu-main li:hover .sub-menu, textarea, input, .banner-small-title a, .comment-list .comment-avatar, .ac-featured-posts .thumbnail .details {
      background-color: #111;
  }
  fieldset, .container, .menu-main > li, .mobile-menu > li, .mobile-menu .sf-sub-indicator, .menu-wrap .search-button, .menu-wrap .browse-more, .mobile-menu-button, .header-wrap, .menu-wrap, .menu-main, .sidebar, .main-page-title, .post-template-1 .details .p-share .contents .close-this-temp1, .posts-pagination, .page-links-wrap, .posts-pagination .paging-wrap, .page-links-wrap .page-links, .posts-pagination a, .page-links-wrap a, .page-links-wrap span, .comments-pagination, .comments-pagination .paging-wrap, .comments-pagination a, .posts-pagination span.current, .posts-pagination span.dots, .tabs-widget-navigation ul li a, .mini-sidebar.browse-window-opened, .browse-by-wrap, .browse-window-opened:after, #wp-calendar, #wp-calendar tbody td, #wp-calendar thead th, .menu-main .sub-menu, .menu-main .sub-menu a, .single-template-1 .details, .single-template-1 .single-content, .single-content blockquote, .comment-text blockquote, .single-content.featured-image:before, .ac-recent-posts li.full-width, .sidebar #recentcomments li, .tagcloud a, .slider-controls, .slide-btn, .slider-pagination a, .as-wrap, .share-pagination, .about-the-author, .about-share .title, .post-navigation, .post-navigation a, .ata-wrap .avatar-wrap, .clear-border, .post-navigation span, .content-wrap, .comments-title, .comment-avatar, .comment-main, textarea, input, li .comment-reply-title small, .menu-wrap .search-submit, .search-wrap.search-visible, .post-template-1 .details .post-small-button, .sidebar-heading, .tabs-widget-navigation, .sidebar .sidebar-heading, .sidebar .tabs-widget-navigation, .ac-popular-posts .position, .ac-twitter-widget-ul li.ac-twitter-tweet, select, table, th, td, pre, .comment-list li.pingback {border-color:#555}
  .menu-wrap a.mobile-menu-button, .menu-wrap a.search-button {color: #FFF}
  .single-template-1 .title, .single-template-1 .single-content, .single-content h1, .single-content h2, .single-content h3, .comments-number, .comments-title, .slider .title a, .slider .title a:hover, .post-template-1 .title a, .post-template-1 .title a:hover, .sidebar-heading, .sidebar #recentcomments li a, .post-template-1 p{color:#DDD}
  ul.menu-main{background-color:#111}
  .menu-main > li > a{color:#FFF}
  .menu-main > li > a:hover{color:#777}
  .menu-main > li.sfHover > a{color:#777}
  .menu-main > li.current-menu-item > a {color:#444}
  .menu-main .sub-menu a:hover{text-decoration:underline; color:#FFF}
  .single-template-1 .details .detail a{color:#BE5656}
  span.prev-post, a.next-post{background-color:#777}
  a.prev-post, a.next-post, .slider-controls{background-color:#555}
  a.prev-post:hover, a.next-post:hover{background-color:#666}
  .post-navigation a.next-post:hover, pre{color:#111}
  .comment-main .vcard .fn{color:#DDD}
  .comment-main .vcard .fn a{color:#BE5656}
  .comment-main .vcard .fn a:hover{color:#BE5656}
  .comment-main a.comment-reply-link{color:#AAA}
  div.comment-text{color:#AAA}
  .comment-reply-title{color:#DDD}
  p.comment-notes{color:#DDD}
  .comment-form-author label, .comment-form-email label, .comment-form-url label, .comment-form-comment label{color:#DDD}
  .form-allowed-tags{color:#DDD}
  code{color:#88F}
  input#submit{background-color:#AD1B1B}
  input#submit:hover{background-color:#AD1515}
  label#subscribe-label, label#subscribe-blog-label{color:#DDD}
  .footer-credits .blog-title{color:#AAA}
  .footer-credits .copyright{color:#888}
  .back-to-top{border-color:#AD1B1B}
  .back-to-top:hover{border-color:#AD1515}
}
