/* ADS */
div.ads_ad_box, div#left_ads, div#ads_left, div.ads_ads_box, div.ads_ads_box2, div.ads_ads_box3, div#bFooter p {
    display: none !important;
}
.wide_column .page_top {
    padding: 15px 20px;
    border-bottom: 0px solid #e7e8ec;
}

/* LEFT MENU */
#side_bar .left_label {
	font-size: 0px
}
#side_bar .left_label:before,
#myprofile_edit_wrap .left_label,
#side_bar a[href*="app"] .left_label,
#side_bar [id*="l_mgid"] .left_label {
	font-size: 12px;
}
#side_bar ol li ul .left_label,
#side_bar a[href*="wall"] .left_label,
#frOpt .left_label,
.vk_custom_item .left_label {
	font-size: 12px !important;
}
.font_medium #side_bar .left_label:before,
.font_medium #myprofile_edit_wrap .left_label,
.font_medium #side_bar a[href*="app"] .left_label,
.font_medium #side_bar [id*="l_mgid"] .left_label {
	font-size: 12px;
}
.font_medium #side_bar ol li ul .left_label,
.font_medium #side_bar a[href*="wall"] .left_label,
.font_medium #frOpt .left_label,
.font_medium .vk_custom_item .left_label {
	font-size: 12px !important;
}
#side_bar a[href*="apps"] .left_label{
	font-size: 0px;
}
.font_medium #side_bar a[href*="apps"] .left_label{
	font-size: 0px;
}
#l_pr .left_label:before {
	content: "Страница";
}
#l_fr .left_label:before {
	content: "Друзья";
}
#l_ph .left_label:before {
	content:"Фотографии";
}
#l_vid .left_label:before {
	content:"Видеозаписи";
}
#side_bar a[href*="audio"] .left_label:before {
	content:"Аудиозаписи";
}
#l_msg .left_label:before {
	content:"Сообщения";
}
#l_nts .left_label:before {
	content:"Заметки";
}
#l_gr .left_label:before {
	content:"Группы";
}
#l_ev .left_label:before {
	content:"Встречи";
}
#l_nwsf .left_label:before {
	content:"Обновления";
}
#l_nws .left_label:before {
	content:"Ответы";
}
#side_bar a[href*="fave"] .left_label:before {
	content:"Закладки";
}
#l_set .left_label:before {
	content:"Настройки";
}
#side_bar a[href*="apps"] .left_label:before {
	content:"Приложения";
}
#side_bar a[href*="docs"] .left_label:before {
	content:"Документы";
}
#l_ads .left_label:before {
	content:"Реклама";
}
#l_spr .left_label:before {
	content:"Поддержка";
}
#side_bar ol li ul .left_label:before {
	display:none !important;
}

/* MESSAGE */
.im-page .im-page--dialogs {
    width: 300px;
    height:80% !important;
}

.im-page .im-page--history {
    height:80% !important;
}

.nim-peer.nim-peer_online .nim-peer--online {
    background: #86a2c4;
}

.im-page .im-page--lastact {
    padding: 15px 15px 15px 15px;
}
._im_typing_name {
    padding: 15px 15px 15px 15px;
}

.im-page .im-page--mess-search {
    width: 300px;
}

/* SIDEBAR */
#side_bar ol {
    width:149px;
    top:60px;
    position: fixed;
}

.left_count_wrap {
    display:none;
}
.left_menu_nav_wrap {
    display:none;
}
.top_profile_img {
    display:none;
}

/* PAGE */
#profile_photos_module {
    display:none;
}
#profile_friends_online {
    display:none;
}
#profile_audios {
    display:none;
}
.post_like_link {
    display:none;
}
.post_reply_link{
    display:none;
}
.wall_module .post_reply_icon {
    display:none;
}
.wall_module .copy_quote {
    border-left: 0px solid #dee6ee;
    padding-left: 5px;
    margin: 5px 0 0;
}
#profile_videos {
    display: none;
}
/* HEADER */
#page_header_cont .back {
    position: absolute;
    width: 100%;
    background: #507299;
    height: 42px;
    margin-top: 0 25px;
    box-sizing: border-box;
}
#page_header {
    position: relative;
    height: 42px;
    margin: 0 auto;
    font-size: 12.5px;
    padding: 0px 15px;
}

  input.text.ts_input
  {
    width: 10px;
    padding: 6px 6px 6px 14px;
    overflow: hidden;
    transition: background-color 0.05s, color 0.05s, width .4s;
  }

  input.text.ts_input:hover
  {
    width: 230px;
    padding: 6px 6px 6px 19px;
    overflow: auto;
  }

  #ts_wrap.ts_wrap .input_back_content
  {
    width: 0 !important;
  }

  #ts_wrap.ts_wrap
  {
    overflow: hidden;
  }

  input.text.ts_input:hover #ts_wrap.ts_wrap
  {
    overflow: auto;
  }

/* MESSAGE */
.im_fwd_log_wrap, .im_wall_log_wrap {
    border-left: 2px solid #eeeeee;
    margin: 0 0 0 3px;
}

/* BUTTONS */
.audio_page_player, .audio_page_player_play .icon, .audio_play {
    border-radius: 3px;
}
.audio_page_player .audio_page_player_play .icon {
    border-radius: 3px;
}
.group_box_image_wrap, .group_box_image_link {
    border-radius: 3px;
}
a.fc_msgs_img {
    border-radius: 3px;
}

/* MUSIC */
.audio_page_layout .audio_friends_list_wrap {
    display:none;
}
.audio_page_layout .has_friends_block .audio_page__rows_wrap {
    padding-right: 20px;
    float: left;
    width: 750px;
    border-right: 0px solid #e7e8ec;
}
.audio_row__sound_bars {
    background-color: #000;
    border-radius:3px;
    opacity: 0.5;
}
.audio_w_covers .audio_row .audio_row__cover_back{
    border-radius: 0px;
    background-image: url(https://vk.com/images/audio_row_placeholder.png);
}
.top_audio_player .top_audio_player_title_wrap {
    display:none;
}
.top_audio_player {
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    color: #d7e2ec;
    padding-right: 10px;
    position: relative;
    max-width: 89px;
    cursor: pointer;
    height: 42px;
    display: none;
    opacity: 0;
    pointer-events: none;
}
.top_audio_player .top_audio_player_play .top_audio_player_btn_icon {
    width:22px;height:22px
}
.top_audio_player .top_audio_player_play {
    padding:1px;margin-top:9px
}
.top_audio_player.top_audio_player_playing .top_audio_player_play {
    animation-name:player_play;
    animation-duration:2.5s;
    animation-iteration-count:infinite;
    animation-timing-function:linear;
    overflow:visible;
    border-radius:100%
}
@keyframes player_play {
    0%{box-shadow:none}50%
    {box-shadow:0 0 0 15px rgba(0,0,0,.2);
        background:rgba(0,0,0,.2)}100%{box-shadow:0 0 0 30px rgba(0,0,0,0);
            background:rgba(0,0,0,0)
    }
}
}
