css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.eve-rave.ch") {
}

@-moz-document domain(eve-rave.ch) {

/* =================== A L L G E M E I N E S [ anfang ] =================== */

* {
	outline: none;
}

body {
	font-family: 'Trebuchet MS', 'Lucida Grande', Verdana, Arial, Sans-Serif;
	color: #9de0ff;
	/*color: #1adc88;*/
	text-shadow: 0px 0px 5px #000;
	background:
		url(http://imagizer.imageshack.us/v2/638x156q90/674/IQOMjP.png),
		url(http://imagizer.imageshack.us/v2/50x50q90/633/NHH6Ho.png),
		linear-gradient( #121929 0%, #002039 80%)
		;
	background-position: left top , left top;
	background-repeat: no-repeat, repeat;
	background-color: #121929;
}

/* =================== A L L G E M E I N E S [ ende ] =================== */

/* =================== W R A P P E R [ anfang ] =================== */

#wrapheader {
	background: url(http://imagizer.imageshack.us/v2/150x161q90/537/FLkl8S.png) !important;
	background-position: left top !important;
	background-repeat: no-repeat !important;
	height: 154px !important;
	width: 100% !important;
}

/* =================== W R A P P E R [ ende ] =================== */

/* =================== F O R E N - N A V I G A T I O N [ anfang ] =================== */

#forumnavi {
	background: transparent !important;
	position: absolute;
	top: 10px;
	right: 20px;
	height: 10px;
}

#forumnavi a {
	margin: 0 0 0 -30px;
}

/* =================== F O R E N - N A V I G A T I O N [ ende ] =================== */

/* =================== S E A R C H B A R  [ anfang ] =================== */

.searchbar {
	position: absolute;
	top: 140px;
	right: 31px;
	height: 10px;
}

/* =================== S E A R C H B A R [ ende ] =================== */

/* =================== B R E A D C R U M B S [ anfang ] =================== */

p.breadcrumbs {
	margin: 0 0 0 0;
}

/* =================== B R E A D C R U M B S [ ende ] =================== */

/* =================== F O R E N Ü B E R S I C H T [ anfang ] =================== */

.catdiv {
	background: transparent !important;
}

.keinrahmen {
	background: transparent !important;
}

td.cat {
	background: #121929 url(http://imagizer.imageshack.us/v2/527x27q90/r/538/X3wTW1.jpg) no-repeat top left;
	border-radius: 5px;
	box-shadow: 1px 1px 2px #000f2e;
	padding-left: 10px;
}

.cat a {
	color: #9de0ff !important;
	background: transparent !important;
}

h4 {
	display: inline-block;
	background: transparent !important;
	padding: 0 0 0 25px;
}

.topicfolder, .topicreply, .topictime  {
	border-radius: 5px;
	box-shadow: 1px 1px 2px #000f2e;
	padding-left: 10px;
	background: rgba(31, 44, 63, .4);
}

.topicautor, .topicviews {
	border-radius: 5px;
	box-shadow: 1px 1px 2px #000f2e;
	padding-left: 10px;
	background: rgba(18, 25, 41, .4);
}

.topicfolder {
	border-radius: 5px;
	box-shadow: 1px 1px 2px #000f2e;
	background: rgba(18, 25, 41, .4);
}

/* =================== F O R E N Ü B E R S I C H T [ ende ] =================== */

/* =================== T A B L E S [ anfang ] =================== */

table {
	border: 0 !important;
	border-spacing: 4px;
  border-collapse: separate;
	background-color: transparent !important;
}

.tablebg {
	background: transparent !important;
}

table th {
	display: none;
}

table .row1 {
	border-radius: 5px;
	box-shadow: 1px 1px 2px #000f2e;
	padding-left: 10px;
	background: rgba(31, 44, 63, .4);
}

table .row2 {
	border-radius: 5px;
	box-shadow: 1px 1px 2px #000f2e;
	background: rgba(18, 25, 41, .4);
}

table .row3 {
	border-radius: 5px;
	box-shadow: 1px 1px 2px #000f2e;
	/*background-color: #003a52;*/
	background: rgba(31, 44, 63, .4);
}

table .row4 {
	border-radius: 5px;
	box-shadow: 1px 1px 2px #000f2e;
	background: rgba(18, 25, 41, .4);
}

.row5 {
	border-radius: 5px;
	box-shadow: 1px 1px 2px #000f2e;
	background: rgba(18, 25, 41, .4);
}

/* =================== T A B L E S [ ende ] =================== */

/* =================== U S E R [ anfang ] =================== */

/* =================== U S E R [ ende ] =================== */

/* =================== P O S T S - V E R F A S S E N [ anfang ] =================== */

textarea {
	padding: 5px;
	background: black;
	color: #00aeff;
	text-shadow: 0px 0px 5px #00aeff;
	border: 1px solid #00aeff;
	box-shadow: 1px 1px 2px #000f2e;
}

input {
	background: black !important;
	color: #00aeff !important;
	text-shadow: 0px 0px 5px #00aeff !important;
	border: 1px solid #00aeff !important;
	box-shadow: 1px 1px 2px #000f2e !important;
}

input:hover {
	background: black !important;
	color: #82d7ff !important;
	text-shadow: 0px 0px 5px #82d7ff !important;
	border: 1px solid #00aeff !important;
	box-shadow: 1px 1px 2px #000f2e !important;
}

option {
	background: black !important;
	color: #00aeff !important;
	text-shadow: 0px 0px 5px #00aeff !important;
	border: 1px solid #00aeff !important;
	box-shadow: 1px 1px 2px #000f2e !important;
}

/* =================== P O S T S - V E R F A S S E N [ ende ] =================== */

/* =================== P O S T S - N A V I G A T I O N [ anfang ] =================== */

td.nav {
	height: 10px !important;
}

.nav, .nav a {
	color: #005784 !important;
}

.nav a {
	background: transparent !important;
}

.cat {
	background: transparent;
}


/* =================== P O S T S - N A V I G A T I O N [ ENDE ] =================== */

/* =================== P O S T S [ anfang ] =================== */

#pagecontent {
	margin: 0 0 0 0;
	padding: 0 0 0 0 !important;
}

.postbody {
	color: #79c0f6;
	text-shadow: 0px 0px 4px #000;
	background: transparent !important;
	padding: 15px 20px 0 20px;
}

.postdetails {
	color: #9de0ff !important;
	background: transparent !important;
}

.spacer {
	display: none;
}

/* =================== P O S T S [ ende ] =================== */

/* =================== P O S T - Z I T A T [ anfang ] =================== */

.postbody div {
	border-radius: 7px;
	border-color: #00577c;
	box-shadow: 1px 1px 2px #000f2e;
	padding-left: 10px;
	background: rgba(31, 44, 63, .6);
}

.quotetitle {
	color: #32647b;
	background: transparent;
}

.quotecontent {
	color: #4f94b4;
	background: #1f2c3f !important;
}

/* =================== P O S T - Z I T A T [ ende ] =================== */

/* =================== L I N K F A R B E N [ anfang ] =================== */

a {
	color: #00aeff;
	text-shadow: 0 0 3px #000;
	text-decoration: none !important;
}

a:hover {
	color: #00aeff !important;
	text-shadow: 0 0 5px #00aeff !important;
	text-decoration: none !important;
}

#forumnavi a {
	color: #1b6e86 !important;
	text-shadow: 0 0 3px #000;
	text-decoration: none !important;
}

.searchbar a {
	color: #00aeff;
	text-shadow: 0 0 3px #000;
	text-decoration: none !important;
}

#forumnavi a:hover, .searchbar a:hover {
	color: #00aeff !important;
	text-shadow: 0 0 5px #00aeff !important;
	text-decoration: none !important;
}

/* =================== L I N K F A R B E N [ ende ] =================== */

/* =================== A U S G E B L E N D E T [ anfang ] =================== */

#datebar {
	display: none;
}

p.datetime {
	display: none;
}

#logodesc, .sitenavi {
	display: none;
}

.titles {
	display: none;
}

/* =================== A U S G E B L E N D E T [ ende ] =================== */

/* =================== F O R E N R E G E L N [ anfang ] =================== */

.forumrules {
	padding: 5px;
	background: black;
	color: #00aeff;
	border: 1px solid #00aeff;
	box-shadow: 1px 1px 2px #000f2e;
}

.forumrules h3 {
	color: #ff8000;
}


/* =================== F O R E N R E G E L N [ ende ] =================== */

}
