css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mifa.cz"){ 
}

@-moz-document url("http://www.mifa.cz/centrala/"), url("http://mifa.cz/centrala/"), url("http://www.mifa.cz/centrala"), url("http://mifa.cz/centrala") {

div.box-tabulka-tymu { display: none !important; }
div.box-zpravy { visibility: collapse !important; }
div.box-faq { position: relative !important; top: -60px !important; }
div.box-info-o-zapasech { position: relative !important; width: 450px !important; left: 87px !important; }
div.box-info-o-zapasech h2 {width: 446px !important; background-position: center !important; }
.klndr {margin: 0px auto 5px auto !important;}

.info-o-zapasech { text-align: center !important; width: 100% !important; }
.info-o-zapasech td, .info-o-zapasech tr { background: #EAEAEA !important; }
.info-o-zapasech tr.darker td { border: none !important; border-left: 1px #CBCBCB solid !important;; border-right: 1px solid #CBCBCB !important; }
.info-o-zapasech tr.darker td.score { border: none !important; }
.info-o-zapasech td a { color: #323232 !important; }
.info-o-zapasech td .green { color: #323232 !important; }
.info-o-zapasech td.score a { color: transparent !important; background: #EAEAEA url("http://carbone.borec.cz/new/mifa/score_invisible.bmp") !important; background-position: center !important; }
.info-o-zapasech td.score:first-child { background: transparent !important; }

}


@-moz-document url-prefix("http://www.mifa.cz/mifa/info_zapas.php"), url-prefix("http://mifa.cz/mifa/info_zapas.php") {

h2 { display: none !important; }
div.box { position: absolute !important; }
h3.detail-zapasu, table.goly, table.statistics { display: none !important; }
p.play { height: 0px !important; }
div.text { padding-top: 175px !important; }
p.play { padding: 0 0 0 0 !important; }
p.play img { height: 50px !important; position: absolute !important; left: 48% !important; top: 72px !important; }
#skoreH { font-size: 40px !important; color: black; display: table-cell !important; position: absolute !important; left: 180px !important; top: 68px !important; border-color: #5cae0c !important; width: 44px !important; text-align: center !important; }
#skoreA { font-size: 40px !important; color: black; display: table-cell !important; position: absolute !important; right: 180px !important; top: 68px !important; border-color: #c73333 !important; width: 44px !important; text-align: center !important; }
div.skore { height: 35px !important; background: #f1f1f1 !important; padding: 13px 3px 0px 3px !important; border-style: solid !important; border-width: 5px !important; }
div.probiha { font-size: 20px !important; color: black; position: relative !important; top: -80px !important; text-align: center !important; }
#strelci { font-family: monospace !important; position: absolute !important; top: 168px !important; padding-left: 30px !important; padding-right: 30px !important; font-size: 12px; text-decoration: overline !important; }
p.prubeh-H { font-variant: small-caps !important; }
p.prubeh-A { font-variant: small-caps !important; }
.konec, .polocas { visibility: hidden !important; }

}
