css:
@-moz-document url(http://www.9lives.be/forum),
url-prefix("http://www.9lives.be/forum") {
   body {
    background: #505050 !important;
    padding: 0 0 10px 0 !important;
}

/* **** HEADER **** */

#header {
    margin: auto !important;
    position: relative !important;
    width: 1000px !important;
    z-index: 10 !important;
}


/* **** WIDTH FORUM **** */

.ctrContentWrapper {
    margin: auto !important;
    width: 1000px !important;
}

/* **** THREAD READ/UNREAD */
	/* |-- READ */

.title {
    font-weight: normal !important;
}

	/* |-- UNREAD */

.threadtitle_unread {
    font-weight: bold !important;
}

/* **** FONT SIZE **** */

ul.floatcontainer, 
div.signaturecontainer, 
#wgo_onlineusers, 
#wgo_stats, 
#wgo_legend, 
#wgo_birthdays, 
div.forumhead h2, 
div.forum_info.block, 
div.thread_info.block,
.moderators h4,
.subforums h4,
#postlist,
.blockhead h2 {
    font-size: 13px !important;
}

/* **** THREAD POST **** */

.postcontent a {
	color: #eb4024 !important;
}
  
.bbcode_postedby strong {
	color: #eb4024;
}

/* **** SET USERNAME OF NORMAL USERS BACK TO WHITE **** */

.userinfo .username strong {
	color: #909090 !important;  
}

/* **** THREAD POST PREVIEW *** */

blockquote.preview,
.checkradio label {
	color: #909090 !important;  
}

/* **** MISC **** */

.lastposttitle {
	margin: 0 !important;
}

.forumdescription {
	line-height: 1.2em !important;
}

.popupbody a {
 	color: #000 !important;   
 }
 

}
