css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.gamersfirst.com") {

    div.clearfix {
        background-color: #1f1f1f !important;
        color: #949494 !important;
        border-color: #000000 !important;
    }
    

    td.col_c_post {
        background-color: #1f1f1f !important;
    }

    h1.ipsType_pagetitle {
        background-color: #242424 !important;
        color: #b3b3b3 !important;
    }

    td.col_c_stats.ipsType_small {
        background-color: #1f1f1f !important;
    }

    td.col_c_forum {
        background-color: #1f1f1f !important;
    }

    td.col_c_icon {
        background-color: #1f1f1f !important;
    }

    ol.breadcrumb.top.ipsList_inline {
        background-color: #1f1f1f !important;
    }

    ol.ipsList_inline.ipsType_small.subforums li a {
        color: #9e9e9e !important;
    }

    td.col_f_content {
        background-color: #1f1f1f !important;
    }

    ul.last_post.ipsType_small li {
        background-color: #1f1f1f !important;
    }

    td.col_f_views.desc.blend_links {
        background-color: #1f1f1f !important;
    }

    div.announcements {
        background-color: #1f1f1f !important;
    }

    ul.ipsList_inline.ipsType_small {
        background-color: #1f1f1f !important;
    }

    div.ipsFilterbar.maintitle {
        background-color: #1f1f1f !important;
    }

    li.disabled span {
        background-color: #1f1f1f !important;
    }

    td.col_f_icon.altrow.short {
        background-color: #1f1f1f !important;
    }

    td.col_f_post {
        background-color: #1f1f1f !important;
    }

    td.col_f_preview.__topic_preview {
        background-color: #1f1f1f !important;
        color: #ffffff !important;
    }

    li.page a {
        background-color: #303030 !important;
        color: #ffffff !important;
    }

    div.post.entry-content p {
        color: #dbdbdb !important;
    }

    div.signature p {
        color: #bfbfbf !important;
    }

    h3.row2 {
        background-color: #0d2d54 !important;
    }

    blockquote.ipsBlockquote.built {
        background-color: #363636 !important;
    }

    p.citation {
        background: #363636 !important;
        color: #000000 !important;
    }

    a.snapback.right img {
        background-color: #575757 !important;
        color: #f520f5 !important;
    }

    strong.bbc {
        color: #969696 !important;
    }

    div.post.entry-content {
        color: #bdbdbd !important;
    }

    ul.ipsForm.ipsForm_vertical.ipsPad {
        background-color: #1f1f1f !important;
    }

    fieldset.submit.clear {
        background-color: #1f1f1f !important;
    }

    input.input_submit.alt {
        background-color: #1f1f1f !important;
        color: #f2f2f2 !important;
    }

    h3.bar {
        background-color: #1f1f1f !important;
        color: #cfcfcf !important;
    }

    li.prev a {
        background-color: #303030 !important;
        color: #ffffff !important;
    }

    li.first a {
        background-color: #303030 !important;
        color: #ffffff !important;
    }
  
}
