css:
@-moz-document domain('trello.com') {
/* Display cards number on Trello Cards */
/* this hack from jamesr: https://userstyles.org/styles/79880/trello-card-ids-small */
.card-short-id{
	display: inline !important;
	border: 1px solid #d9d9d9;
	border-bottom-color: #d3d3d3;
	border-radius: 2px;
	color: #737373;
	cursor: default;
	float: left;
	font-size: 12px;
	line-height: 16px;
	height: 16px;
	margin-right: 4px;
	padding: 0 4px;
	position: relative;
	text-decoration: none;
}

/* Display how many cards are there on column header */
.list-header-num-cards{
	display: inline !important;
}

/* Hide card cover images on Trello boards */
.list-card-cover, .js-card-cover{
	display: none !important;
}

/* Display filter button for easy and quick access */
.board-header-btn-filter-indicator{
	display: block !important;
	width: 40px;
}

/* Hide default filter button text */
.board-header-btn-filter-indicator > .board-header-btn-text {
	position: absolute;
	visibility: hidden;
}

/* Replace default filter button text */
.board-header-btn-filter-indicator > .board-header-btn-text:before {
	content: "Filtres";
	visibility: visible;
}

/* Display extra details by default */
.hide-extra-actions .phenom-other{
	display: block !important;
}

/* Hide filter close button
.board-header-btn-filter-indicator > .board-header-btn-icon-close{
	display: none !important;
} */
}
