@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("userstyles.org") {
*
{
	font-family: Verdana !important;
}
LI
{
}
BUTTON
{
	background-color: #7B7B7B !important;
	border: 1px inset #616161 !important;
	-moz-border-radius: 5px !important;
	color: #FFFFFF !important;
}
BUTTON:hover
{
	background-color: #252525 !important;
	border: 1px dashed #FFFFFF !important;
}
LI:hover
{
	background: #858585 !important;
}
#stylish-not-installed:hover
{
	background: #7D7D7D !important;
}
#category-search-submit
{
	background-color: #8D8D8D !important;
	border: 1px inset #B7B7B7 !important;
	-moz-border-radius: 5px !important;
	color: #FFFFFF !important;
}
#category-search-submit:hover
{
	background-color: #2F2D2D !important;
	border: 1px dashed #FFFFFF !important;
}
#style-meta DIV:hover
{
	background-color: #7B7B7B !important;
}
#category-search-terms
{
	background-color: #515151 !important;
	-moz-border-radius: 3px !important;
	border: 1px outset #717171 !important;
}
#category-search-terms:hover
{
	background-color: #8F8F8F !important;
	-moz-border-radius: 3px !important;
	border-color: #FFFFFF !important;
	border: 1px outset #FFFFFF !important;
}
#style-author
{
	color: #FFFFFF !important;
}
#detail-average-rating
{
	background: #FFFFFF !important;
	width: 85px !important;
	height: 20px !important;
	padding: 0 !important;
	margin: 0 !important;
}
LI
{
	color: #FFFFFF !important;
}
H2
{
	color: #6D6D6D !important;
}
BODY
{
	padding: 0 30px !important;
	background: #1B1B1B !important;
	margin: 0 !important;
}
H1
{
	margin-bottom: 0 !important;
	color: #C5C5C5 !important;
}
PRE
{
	border: 1px dashed black !important;
	background-color: #F9F9F9 !important;
	padding: 10px !important;
	overflow: auto !important;
}
CODE
{
	font-family: monospace !important;
}
H3
{
	font-size: medium !important;
	padding-bottom: 0 !important;
}
#style-show
{
	color: #777777 !important;
}
A
{
	color: #DDDDDD !important;
}
A:hover
{
	color: #2F2D2D !important;
	background-color: #777 !important;
}
A:visited
{
	color: #C5C5C5 !important;
}
A:visited:hover
{
	color: #CFCFCF !important;
}
A IMG
{
	border: 0 !important;
}
#logo
{
	float: left !important;
	margin: 0.05em 0 0 1em !important;
	font-size: xx-large !important;
	font-weight: 900 !important;
}
#logo A, #logo A:visited
{
	text-decoration: none !important;
	color: white !important;
}
#logo A:hover
{
	cursor: not-allowed !important;
	-webkit-user-drag: none !important;
}
#header-bar
{
	margin-top: 0 !important;
	background-color: #717171 !important;
	padding: 10px 5px 5px !important;
	-moz-border-radius: 0 0 10px 10px !important;
	height: 3.5em !important;
	border: 1px solid #514F4F !important;
	border-top: 0 solid #FFFFFF !important;
}
#navigation
{
	list-style-image: none !important;
	margin: 2.3em 0 0 48em !important;
	float: none !important;
	clear: right !important;
	padding: 0 !important;
}
#navigation LI
{
	display: inline !important;
	margin: 0 !important;
}
#navigation A, #navigation A:visited, #navigation .current-page
{
	border: 1px solid transparent !important;
	border-bottom: none !important;
	background-color: #A0A3A7 !important;
	color: white !important;
	padding: 2px 10px 4px !important;
	text-decoration: underline !important;
	font-weight: 900 !important;
	-moz-border-radius: 15px 0px 5px 3px !important;
	font: bold 100%/100% "Lucida Grande" !important;
}
#navigation .current-page
{
	border-top: 1px solid #FFFFFF !important;
	border-left: 1px solid #FFFFFF !important;
	border-right: 1px solid #FFFFFF !important;
	background-color: #BBBBBB !important;
	color: #FFFFFF !important;
}
#navigation .current-page:hover
{
	border-top: 1px solid #FFFFFF !important;
	border-bottom: 1px dashed #FFFFFF !important;
	border-left: 1px solid #FFFFFF !important;
	border-right: 1px solid #FFFFFF !important;
	background-color: #CBCBCB !important;
	color: #FFFFFF !important;
}
#navigation A:hover
{
	background-color: #27282B !important;
	cursor: default !important;
	border-right: 1px solid #FFFFFF !important;
	border-left: 1px solid #FFFFFF !important;
	border-top: 1px solid #FFFFFF !important;
	border-bottom: 1px dashed #FFFFFF !important;
}
#search
{
	padding: 1px 4.7em 0 0 !important;
	float: right !important;
	font-size: small !important;
}
#search-terms
{
	padding-left: 2px !important;
	padding-right: 0px !important;
	background-color: #575656 !important;
	border: 1px outset #0D0C0C !important;
	width: 15em !important;
/*border-radius: 5px*/
	-moz-border-radius: 5px !important;
}
#search-terms:hover
{
	background-color: #8F8F8F !important;
	cursor: default !important;
}
#search-terms:focus
{
	background-color: #6D6B6B !important;
}
#search INPUT[type="submit"]
{
	font-weight: bold !important;
	border: 1px inset #878787 !important;
	background-color: #9D9D9D !important;
	color: white !important;
/*border-radius: 5px*/
	-moz-border-radius: 5px !important;
}
#search INPUT[type="submit"]:hover
{
	background-color: #545557 !important;
	border: 1px dashed #FFFFFF !important;
}
.search-default
{
	border-color: black !important; /*[e]black*/
/*border-width: 2px !important;*/
}
#style-list LI
{
	margin-right: 1em !important;
	padding-right: 5px !important;
}
.rule-value
{
	font-style: italic !important;
}
.form-controls TH
{
	text-align: right !important;
	white-space: nowrap !important;
}
TABLE.form-controls, .form-controls TD, TEXTAREA
{
	width: 100% !important;
}
TEXTAREA#css
{
	font-family: monospace !important;
	height: 20em !important;
}
.inline-form
{
	display: inline !important;
}
.list-average-rating, #detail-average-rating
{
	white-space: nowrap !important;
	vertical-align: bottom !important;
}
DIV#obsoletion-message
{
/*for shrink wrap*/
	display: table !important;
	background-color: #FFFFAA !important;
	padding-left: 1em !important;
	padding-right: 1em !important;
	border: 3px solid #FFFF44 !important;
/*border-radius: 10px*/
	-moz-border-radius: 10px !important;
	float: right !important;
	width: 33% !important;
}
.obsolete
{
	text-decoration: line-through !important;
}
#style-meta
{
	padding: 0em 1em 1em 0em !important;
	font-size: smaller !important;
	color: #FFFFFF !important;
}
#sidebar
{
	clear: both !important;
	margin-left: 2em !important;
	float: right !important;
	margin-top: 2em !important;
}
#sidebar H2
{
	font-size: 1em !important;
	margin: 0 !important;
}
#miscellaneous
{
	margin: 1em 0 !important;
}
#donate, #donate INPUT
{
	display: inline !important;
}
#donate INPUT
{
	vertical-align: bottom !important;
}
#sidebar UL
{
	margin: 0 !important;
}
.bug-fixed
{
	text-decoration: line-through !important;
}
#before-screenshot-entry DIV *, #after-screenshot-entry DIV *
{
	vertical-align: middle !important;
}
.screenshot
{
	border: 2px solid black !important;
}
.multiple-screenshot
{
	max-width: 300px !important;
}
.single-screenshot
{
	max-width: 600px !important;
}
.screenshot-caption
{
	font-weight: bold !important;
	font-style: italic !important;
}
.screenshot-caption
{
	color: #C1C1C1 !important;
}
.screenshot-table, .screenshot-table TD, #screenshots
{
	width: auto !important;
	vertical-align: top !important;
}
#stylish-install, #stylish-update, #stylish-not-installed-ie
{
	display: none !important;
}
#hidden-meta
{
	display: none !important;
}
.how-to-use
{
	font-size: smaller !important;
}
#delete-comment-button, #submit-comment-button
{
	float: right !important;
	margin-bottom: 0.5em !important;
	margin-left: 0.5em !important;
}
#delete-comment
{
	margin: 0 1em !important;
}
LABEL.rating
{
	border-width: 1px !important;
	border-style: solid !important;
	border-color: #DDD !important;
/*border-radius: 2px*/
	-moz-border-radius: 5px !important;
	padding: 5px !important;
}
LABEL.rating:hover
{
	background-color: #FF7 !important;
}
#style-comments LI
{
	clear: both !important;
}
.linking-note, .login-note
{
	font-size: x-small !important;
	margin: 0 !important;
	font-style: italic !important;
}
#account-link
{
	color: white !important;
	margin-right: 1em !important;
}
#account-link A
{
	color: #FFFFFF !important;
	cursor: default !important;
}
.data-table
{
	border-collapse: collapse !important;
}
.data-table TH, .data-table TD
{
	vertical-align: top !important;
	border: 1px solid black !important;
	padding-left: 0.5em !important;
	padding-right: 0.5em !important;
}
#google-ad
{
	margin-top: 1em !important;
	width: 728px !important;
	height: 90px !important;
	border: 0 !important;
	margin-left: auto !important;
	margin-right: auto !important;
}
#openid
{
	background-image: url(http://openid.net/login-bg.gif) !important;
	background-repeat: no-repeat !important;
	background-position: 0 50% !important;
	color: #000 !important;
	padding-left: 18px !important;
}
.two-column TD
{
	vertical-align: top !important;
}
.error
{
	font-weight: bold !important;
}
#view-my-comment
{
	display: none !important;
}
.stars
{
	white-space: nowrap !important;
}
.enter-comment
{
	clear: both !important;
}
#install-options CAPTION
{
	margin-top: 1.5em !important;
	margin-bottom: 0.3em !important;
	text-align: left !important;
}
#install-options TD
{
	max-width: 200px !important;
}
#show-button
{
	display: none !important;
}
#style-show H2
{
	margin-top: 0.5em !important;
}
#show-code
{
	margin-top: 0.5em !important;
}
#show-code PRE
{
	margin-top: 0 !important;
}
#view-code
{
	display: none !important;
}
.poor-rank
{
	opacity: 0.3 !important;
}
.good-rank
{
	font-weight: bolder !important;
}
#paypal-donate-by-name
{
	display: none !important;
}
#paypal-donate-by-feedback
{
	display: inline !important;
	vertical-align: top !important;
}
#reviews
{
	padding-left: 2em !important;
}
.category-style-top
{
	float: left !important;
	width: 45% !important;
}
.post-your-style
{
	clear: both !important;
}
.ad
{
	display: none !important;
}
#control-panel
{
	display: none !important;
}
.paypal-donate-image
{
	width: 110px !important;
	height: 23px !important;
}
.all-link
{
	clear: both !important;
}
.post-your-style
{
	text-align: right !important;
}
#openid-login, #regular-login
{
	float: left !important;
	max-width: 45% !important;
	margin-right: 1em !important;
}
#openid
{
	width: 95% !important;
}
#openid-login .remember
{
	float: left !important;
}
#openid-login .login-actions
{
	margin-top: 0.5em !important;
	text-align: right !important;
}
LEGEND
{
	font-weight: bold !important;
}
P:hover
{
	color: #FFFFFF !important;
	background: #8D8D8D !important;
	background-color: #777 !important;
}
P
{
	color: #FFFFFF !important;
}
#reviews .review-container
{
	font-size: smaller !important;
}
}
