css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('http://www\\.rockpapershotgun\\.com/forums.*') {
  
  .activitystream .activitybit, /* What's New */
  .activitystream .content, /* What's New */
  .activitystream .blockbody, /* What's New */
  #threadlist div.nonsticky, /* Subscribtion List - Not Stickies */
  #threadlist div.sticky, /* Subscribtion List - Stickies */
  .forumrow, /* Forum Row */
  .postbody, /* Signatures */
  .quote_container span, /* Quotes */
  .postcontent { /* Post Text */
    color: #00A000 !important;
  }
  .time, /* Thread Time */
  a[id^=yui-gen], /* Thread - Last Posted by */
  a[id^=thread_title], /* Thread Title - Unread */
  .postdetails .postbody a { /* Post Links */
    color: red !important;
  }
  .posthead, /* Post Header */
  .postfoot div, /* Post Footer */
  .postdetails, /* Post Sidebar*/
  .postdetails .userinfo { /* Post Sidebar - User info */
    background: #151515 !important;
  }
  .body_wrapper,
  #wgo .blockbody, /* What's going on */
  .postbody, /* Signatures */
  .postdetails .postbody { /* Post Body */
    border: 0 !important;
    border-radius: 0 0 0 0 !important;
    background: black !important;
  }
  .postdetails .userinfo{ /* Post Sidebar - User info */
    color: green !important;
  }
  .forumtitle a, /* Forum Title */
  .forumlastpost .lastposttitle a, /* Forum - Last Post Thread Title */
  a[id^=thread_title].threadtitle_unread, /* Thread Title - Unread */
  .postdetails .userinfo .username { /* Post Sidebar - User Info - Username */
    color: #DBA716 !important;
  }
  .forumbit_post .forumrow, /* Forum List - Row */
  .activitystream .activitybit, /* What's New */
  .activitystream .content, /* What's New */
  .activitystream .blockbody, /* What's New */
  #threadlist div.nonsticky, /* Subscribtion List - Not Stickies */
  #threadlist div.sticky, /* Subscribtion List - Stickies */
  .threadstats, /* Thread Statistics */
  .blockbody .blockrow /* Subscription List - Sidebar */,
  .blockbody .blockrow :hover /* Subscription List - Sidebar */,
  .quote_container {
    background: #151515 !important;
  }
  
  /* Spoilers */
  font[color="white"],
  font[color="#ffffff"] {
    color:#00A000 !important;
    background: #00A000;
  }
  font[color="white"]:hover,
  font[color="#ffffff"]:hover {
    background: black;
  }
  
  /* Quick Reply box */
  .wysiwyg_block * {
    background: black !important;
  }
  .editor textarea {
    background: #151515 !important;
    color: #00A000 !important;
  }
}
