css:
@-moz-document domain("bato.to") {
#mainContent, .ipsType_pagetitle, .ipsType_subtitle{
background:transparent!important;
color:#e5e5e5!important;
}

#primary_nav, #logo, #themeToggle, .submit_input{
background-color:#333535!important;
}

.cpe, .cpe #community_app_menu > li.active > a{
background-color:#2a2a2a!important;
}

.ipsBox, .maintitle, .ipsBox_container, p.citation, .ipsComment_reply, .general_box, .general_box h3, #zoom_notice, .bar, .submit{
background: url("http://i.imgur.com/NjslJ44.png")!important;
text-shadow:none!important;
}
  
a, .input_submit{
color:#8a8a8a!important;
}
  
.post_controls{
background:linear-gradient(#3a3a3a,#000000)!important;
border:1px solid #1f1f1f!important;
margin-top:39px!important;
}

.ipsButton_secondary, .input_submit, .disabled span, .topic_buttons li a{
background:linear-gradient(#3a3a3a,#000000)!important;
border:1px solid #1f1f1f!important;
}

.header{
background:url("http://i.imgur.com/NjslJ44.png")!important;
color:#e5e5e5!important;
text-shadow:none!important;
}

.ipsComment_wrap, .ipsComment, .ipsBlockquote, .ipsLikeBar_info, .row2, .row1, .row0, .published{
background:transparent!important;
}
  
.clear a, .chapters_list a, .clearfix div a, .bar{
color:#e5e5e5!important;
}
    
p.citation{
color:#a7a7a7!important;
border:1px solid #979797!important;
border-bottom:0px solid #1f1f1f!important
}
    
.ipsBlockquote{
border:1px solid #979797!important;
border-top:0px solid #1f1f1f!important;
}

.ipsLikeButton{
background:#1f1f1f url('https://bato.to/forums/public/style_images/subway/like_button.png') no-repeat 0px 3px!important;
border:1px solid #1f1f1f!important;
}

.general_box h3{
border-bottom:1px solid #1f1f1f!important;
}

.row1, .row2, .row0, .published{
 color:#979797!important;
}
  
.row1 a, .row2 a, .row0 a{
color:#e5e5e5!important;
}

.moderation_bar ul li a img{
display:none!important;
}

.moderation_bar ul li select{
color:#979797!important;
background-color:#1f1f1f!important;
}

.pagination ul li.page a, .pagination ul.ipsList_inline li.next a, .pagination ul.ipsList_inline li.last a{
color:#2a2a2a!important;
}
  
.pagination ul li.active{
color:#e5e5e5!important;
}

.ipsBox div{
background:none!important;
}
  
#comic_wrap div{
right:0px!important;
top:50px!important;
}

#comic_page, #zoom_notice{
border:2px solid #1f1f1f!important;
}

#secondary_navigation, #socialLinks li, .cpe .pagination .pages li.active{
background-color:#333535!important;
}

.border{
background:none!important;
color:#e5e5e5!important;
}

.ipsVerticalTabbed_tabs li, .ipsVerticalTabbed_tabs li.active a{
color:#c7c7c7!important;
background:#3a3a3a!important;
border-color:#000!important;
}
  
.ipsVerticalTabbed_tabs>ul, .ipsBox_container, .member_entry{
border-color:#3a3a3a!important;
}
}

@-moz-document url-prefix("https://bato.to/forums"), url-prefix("http://bato.to/forums") {
.topic_buttons li.important a, .topic_buttons li.important span, .ipsButton .important, .ipsButton.important, .bbc_spoiler_show{
background:#3a3a3a!important;
border-color:#000!important;
}
  
.ipbfs_titlebox, .statistics, .statistics_head{
background: url("http://i.imgur.com/NjslJ44.png")!important;
border-color:#3a3a3a!important;
color:#e5e5e5!important;
}
    
.cpe .post_block h3{
background-color:#2a2a2a!important;
}
        
.ipsBox .ipsBox_container, #anonymous_element_30, .ipsSideBlock, #anonymous_element_31, .ipsSideBlock h3{
background-color:transparent!important;
background: url("http://i.imgur.com/NjslJ44.png")!important;
}
            
.border{
background:none!important;
color:#e5e5e5!important;
}
          
.edit{
background:rgba(255,235,200,0.5) url('https://bato.to/forums/public/style_images/subway/comment_edit.png') no-repeat 6px 10px!important;
text-shadow:none!important;
color:#e5e5e5!important;
border-color:#e5e5e5!important;
}
            
span, .bbc_spoiler_show, .ipsSideBlock, #anonymous_element_30, #anonymous_element_31, .ipsSideBlock h3{
color:#e5e5e5!important;
}
            
.post_controls{
width:95px!important;
}

.col_c_icon img{
visibility:hidden!important;
}

#secondary_navigation, #socialLinks li, .cpe .pagination .pages li.active, .ipsBadge_green, .cpe .mini_pagination a{
background-color:#333535!important;
}
}

@-moz-document url-prefix("https://bato.to/chat"), url-prefix("http://bato.to/chat") {
#content div{
background: url("http://i.imgur.com/NjslJ44.png")!important;
text-shadow:none!important;
color:#e5e5e5!important;
}
}

@-moz-document url-prefix("https://bato.to/search"), url-prefix("http://bato.to/search") {
#content form div div{
background: url("http://i.imgur.com/NjslJ44.png")!important;
color:#e5e5e5!important;
}
}

@-moz-document url-prefix("http://bato.to/group"), url-prefix("https://bato.to/group") {
.col_c_icon a img{
visibility:hidden!important;
}
  
.col_c_icon img{
visibility:hidden!important;
}

div form div input[type=submit]{
background-color:#3a3a3a!important;
}
}

@-moz-document url-prefix("http://bato.to/forums"), url-prefix("https://bato.to/forums") {
.message.unspecific{
background-color:rgba(243,243,243,0.2)!important;
border-color:#e5e5e5!important;
color:#e5e5e5!important;
}
    
.short img{
visibility:hidden!important;
}
}
