#tabbrowser-tabs>arrowscrollbox>scrollbox>box{
background: transparent !important;
}
.tabbrowser-tab {
border-bottom: 1px solid  !important;
background: transparent !important;
font-family: sans-serif !important;
font-size: 12px !important;
/*text-shadow: 0px 0px;*//*makes bolder but not as bold as bold*/
 }
.tabbrowser-tab[selected="true"] {
background: transparent !important;
border:none !important;
/*color:  #a0d569 !important;*/
font-weight: bold !important;
} 
.tabbrowser-tab[pinned][selected]{
}
tab:hover, .tabbrowser-tab[pinned]:hover{
background: transparent !important; }
.tabbrowser-tab[pinned] {
-moz-box-ordinal-group: 1!important;
background-color: transparent !important;
border: none !important;
float:right !important; /*somehow removes text label*/
padding: 2px 3px 0px 3px !important;
/*-moz-box-ordinal-group: 1!important;*//* 1 is default*/
}
.tabs-newtab-button {
/*-moz-box-ordinal-group: 4!important;*//*4 put at bottom of tabs 2(top) is default*/
}
