css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
url-prefix(http://news.163.com),url-prefix(http://money.163.com){
/*修改*/
 h2.bigsize {
    font-family: "Microsoft YaHei","微软雅黑","宋体";
    font-size: 18px;
    font-weight: bold;
    height: 24px;
    line-height: 24px;
    margin: 11px 0px 12px;
    overflow: hidden;
}    
.main-list li{line-height: 30px;}  
.main-col-11 p, .area-sub p, .sub-col-6 p, .sub-col-8 p, .news-imgs p {line-height: 32px; }
.wrapperbg {height: 12px;} 
.nph_bg {padding: 1px 0px;}
    
.nph_photo_view{width: 960px !important;}
.nph_gallery {
    text-align: center;
    word-wrap: break-word;
      position: relative;
  top: -60px;
  left: 30px;
    padding: 1px 0px;
    font-weight: normal;
}  


/*隐藏*/  
/*头部广告*/
.finance_header_gg,  
.top-gg-area,.ep-area,
/*右栏*/
.ep-content-side,
/*正文下的聚合阅读*/
.extra-tag,
/*回到新闻/网易首页*/
.ep-returnlink,
/*关键词阅读*/
#js-epTabNews,
/*扩展阅读*/
.ep-tjiframe,
/*正文底部大菜单*/
#js_N_nav_footer,
/*空白间隔*/
.blank20,.blank9,.blank6,
/*正文底部广告等*/
.sharecommend-wrap,iframe[width="590"],.ep-keywords,#js-ep-bobo,img[width="262"],.ep-content-main .ep-main-parting
{display: none !important;}


/*新闻首页去掉头部广告后压缩头部的空间*/
.wrapperbg{height:141px !important;}
/*新闻主页*/
h2.bigsize, h3.bigsize { font-family: "方正宋黑简体","黑体" !important;}
/*新闻正文页*/
/*左栏主框架*/
.ep-content-main {width:100% !important;}
/*左栏次框架*/
#endText{width:100% !important;}
.ep-content-bg {background:none !important;}
/*正文标题*/
h1.ep-h1{
  font-family: "方正宋黑简体","黑体" !important;
  font-size: 25px !important; 
  line-height: 1.5em !important;
  text-align:center;
}
/*正文标题下发布时间、来源、参与人数、分享到*/
.ep-info {float:right !important;}

p.ep-summary {
  width:80% !important;
  text-align:center;
  margin:0 auto !important;
}
/*调整正文中内嵌视频*/
.video {width:70% !important;height:504px !important;margin:auto;}
#endText .video-info *{font-size:16px !important;}
/*正文段落*/
#endText p { 
  font-size: 16px !important; 
  line-height: 1.5em !important;
  font-family: 文泉驿微米黑,黑体 !important;
}

}
  
@-moz-document domain("view.163.com") {
.colR {
  display: none !important;}
.areabg1 {
  background: none !important;}
.colLM {
  width:960px !important;}
.feed-text p {
  font-size: 16px !important; 
  line-height: 1.5em !important;
  font-family: 文泉驿微米黑,黑体 !important; }
}
