@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("macserbia.org") {
  body > div > table
  {
    width: 90% !important;
    border: 4px solid #ccc;
  }
  .outline
  {
    padding: 5px;
    position: relative;
  }
  #pathway_outer
  {
    float: none;
    width: 100%;
  }
  #search_outer
  {
    top: 5px;
    right: 268px;
    float: none;
    position: absolute;
  }
  #header_outer
  {
    position: relative;
  }
  #header
  {
    width:100%;
    display: block;
    background-position: right top;
  }
  #top_outer
  {
    position: absolute;
  }
  #left_outer
  {
    float: left;
    clear: right;
  }
  #content_outer
  {
    float: none;
    width: auto;
    margin-left: 180px;
  }
  #banner_inner
  {
    height: 80px
  }
  table.contentpane
  {
    font-family: 'Open Sans', sans-serif;
  }
  .contentpane > tbody > tr > td.sectiontableheader
  {
    padding: 10px;
  }
  .contentpane > tbody > tr > td:nth-child(1).sectiontableheader, .contentpane > tbody > tr > td:nth-child(2).sectiontableheader, .contentpane > tbody > tr > td:nth-child(3).sectiontableheader
  {
    width: auto;
