  }
  .contentpane > tbody > tr > td:nth-child(4).sectiontableheader
  {
    width: 50%;
  }
  .contentpane > tbody > tr > td:nth-child(5).sectiontableheader, .contentpane > tbody > tr > td:nth-child(6).sectiontableheader
  {
