  tr.sectiontableentry2 > td, tr.sectiontableentry1 > td
    {
      font-size: 12px;
    }
  tr.sectiontableentry2 > td:nth-child(1), tr.sectiontableentry1 > td:nth-child(1), tr.sectiontableentry2 > td:nth-child(2), tr.sectiontableentry1 > td:nth-child(2), tr.sectiontableentry2 > td:nth-child(3), tr.sectiontableentry1 > td:nth-child(3)
  {
