  tr.sectiontableentry2 > td:nth-child(4), tr.sectiontableentry1 > td:nth-child(4)
    {
      padding: 10px;
    }
  tr.sectiontableentry2 > td:nth-child(5), tr.sectiontableentry1 > td:nth-child(5), tr.sectiontableentry2 > td:nth-child(6), tr.sectiontableentry1 > td:nth-child(6)
    {
      text-align: center;
      padding: 0 10px;
    }
    tr.sectiontableentry2 > td:nth-child(7), tr.sectiontableentry1 > td:nth-child(7), tr.sectiontableentry2 > td:nth-child(8), tr.sectiontableentry1 > td:nth-child(8)
    {
      padding: 5px 10px;
    }
    tr.sectiontableentry2 > td:nth-child(8) > table, tr.sectiontableentry1 > td:nth-child(8) > table
    {
      width: 100%;
    }
  tr.sectiontableentry2 > td:nth-child(8) > table > tbody > tr > td, tr.sectiontableentry1 > td:nth-child(8) > table > tbody > tr > td
    {
      width: auto;
     	white-space: nowrap;
    }
    tr.sectiontableentry2 > td:nth-child(8) > table > tbody > tr > td:last-child, tr.sectiontableentry1 > td:nth-child(8) > table > tbody > tr > td:last-child
    {
      width: auto;
      padding: 0 5px;
     	white-space: nowrap;
    }
  tr.sectiontableentry2 > td:nth-child(8) > table > tbody > tr > td > small, tr.sectiontableentry1 > td:nth-child(8) > table > tbody > tr > td > small
    {
      font-size: 12px;
    }

  
  br + table.contentpane > tbody > tr > th.sb_catname
  {
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    padding: 6px 12px;
    background: url('data:image/png;base64,') right 10px center no-repeat #ccc;
  }
  br + table.contentpane > tbody > tr > td.sectiontableheader
    {
      padding: 3px;
      text-align: left;
    }
  br + table.contentpane > tbody > tr > td.sectiontableheader > strong
  {
    color: #999;
    font-weight: normal;
    letter-spacing: 1px;
    font-family: 'Open Sans', sans-serif;
  }
  br + table.contentpane > tbody > tr > td.sectiontableheader:nth-child(1)
  {}
  br + table.contentpane > tbody > tr > td.sectiontableheader:nth-child(2)
  {
    width: 50%;
