  }
  br + table.contentpane > tbody > tr > td.sectiontableheader:nth-child(3), br + table.contentpane > tbody > tr > td.sectiontableheader:nth-child(4), br + table.contentpane > tbody > tr > td.sectiontableheader:nth-child(5)
  {
