  }

  br + table.contentpane > tbody > tr.sectiontableentry2 > td span.createdate, br + table.contentpane > tbody > tr.sectiontableentry1 > td span.createdate
  {
    width: auto;
    padding: 0;
    color: #666;
    font-size: 11px;
    font-weight: normal;
    white-space: nowrap;
  }
  
}
