@-moz-document url(http://www.9lives.be/forum),
url-prefix("http://www.9lives.be/forum") {
#header {margin: auto !important; position: relative !important; width: 1024px !important; z-index: 0 !important; border: none !important;}
.header-menu { height:50px !important; background: #b94a48 !important; }
.header-menu .title a { height:50px !important; background: #b94a48 url(https://dl.dropbox.com/u/4224739/9l/9logo.png) no-repeat !important;; border: none !important; }
body { background: #191919 !important; font-family: Tahoma !important; font-size: 11px !important; padding: 0 0 10px 0 !important; }
body a, .popupmenu a { color: #eee !important; }
.forumtitle, .moderators, .threadtitle, .threadmeta, .threadlastpost, .pagination, .popupmenu, #thread_controls, #wgo, #footer, .blocksubhead, .forumhead .forumthreadpost, .forumhead .forumlastpost, .options_input_block .group input, h4, .commalist, h6 { font-family: Tahoma !important; font-size: 11px !important; }
.body_wrapper { margin-top: -4px !important; background: #191919 !important; }
#sidebar_container { background-image: none !important; background: #2d2d2d !important; border: 1px solid #444 !important; border-radius: 0px !important; }
.moduleinactive_bg { background-image: none !important; background: #2d2d2d !important; border: none !important; }
#member_content, #sidebar_container, #sidebar_container .textcontrol { font-family: Tahoma !important; font-size: 11px !important; }
.userprof_module { background: #b94a48 !important; border: 1px solid #b94a48 !important; }
.userprof_content.userprof_content_border { background: #2b2b2b !important; border: 1px solid #444 !important; }
.userprof_content.userprof_content_border p { color: #444 !important; }
#activity_tab_container div, .as-tabs dd, .memberprofiletabunder, #profile_swap_button, #moreactivity_container, .userprof_headers_border { background: #b94a48 !important; border: none !important; font-family: Tahoma !important; font-size: 11px !important; }
.userprof_moduleinactive { background: #222 !important; border: 1px solid #2b2b2b !important; }
.userprof_module a, .userprof_moduleinactive a { color: #fff !important; font-family: Tahoma !important; font-size: 11px !important; }
.activitybit.forum_post.userprof_content.userprof_content_border { background: #2b2b2b !important; border: 1px solid #444 !important; font-family: Tahoma !important; font-size: 11px !important; }
#view-aboutme dt, #view-aboutme dl, #view-aboutme dd, #view-aboutme h5, #view-aboutme p, #view-aboutme span { color: #fff !important; }
.forumlastpost img { position: absolute !important; margin-left: 3px; }
.activitybit .content { background: #2d2d2d !important; }
#activitylist a, .forumdescription a, .pagination span a:hover, .pagination span.selected a, .threadlastpost strong, .forumlastpost strong, .thread_info_block a:link, .thread_info_block a:visited, .popupbody.filter .selected { color: #b94a48 !important; }
.header, .above_body, #navtabs .floatcontainer, #navtabs, .menu, #searchtypeswitcher .selected a { background: #b94a48 !important; }
#vbtab_forum, #vbtab_whatsnew, #vbtab_blog { background: #2d2d2d !important; }
#navtabs li, #navbar_search span { border: none !important; }
#globalsearch { margin-right:10px !important; }
.textboxcontainer, .buttoncontainer { background: #2d2d2d !important; }
.buttoncontainer { padding-left:2px !important; padding-right:2px !important; }
#navbar { margin-top:0px !important; }
#vbflink_newposts { margin-left:5px !important; }
#navtabs { border-left:10px solid #b94a48; border-right:10px solid #b94a48; border-bottom:none; padding-left:0px !important; }
#navtabs .floatcontainer { padding-bottom:1px !important; border-top:none !important; }
.textbox { border: none !important; margin-top:1px !important; background: #2d2d2d !important; width: 140px !important; height: 18px !important; }
#navtabs .selected a { color: #fff !important; background: none !important; }
#navtabs a:hover { color: #fff !important; text-decoration: underline; }
.navtab { background: #2d2d2d !important; border: none !important; border-right: 10px solid #b94a48 !important; }
#toplinks { position: absolute !important; top:-27px !important; right: -4px !important; }
select option { background-color: #2d2d2d !important; }
#searchtypeswitcher { border-bottom: 5px solid #b94a48 !important; }
.blockbody.formcontrols h3 { background: #b94a48 !important; color: #fff !important; }
#searchtypeswitcher a { background: #222 !important; }
.pagination.popupmenu.nohovermenu img, .navbithome img { filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/></filter></svg>#grayscale"); -webkit-filter: grayscale(100%); }
.postbit.blockrow .header { background: #b94a48 !important; height: 15px !important; padding: 10px !important; color: #fff !important; }
.postbit.blockrow .content { background: #2d2d2d !important; padding: 10px !important; }
.postbit.blockrow .content .quote_container { background: #222 !important; }
#thread_controls, .textcontrols, #footer, #above_threadlist_controls, .subforumdescription { background: #191919 !important; border: none !important; }
#above_threadlist_controls, #thread_controls { float: right; margin-top: -28px; }
#thread_controls a, .content, .userinfo { color: #ccc !important; }
.lastposttitle .prefix, .threadtitle .prefix, .threadtitle img { color: #666 !important; }
.lastposttitle .postimg, #wgo_legend, .view_button.light.active, .menu, h2.blockhead, .threadpagestats, .subforumdescription, #postpagestats_above { display: none !important; }
#breadcrumb a, .threadtitle a, .userinfo_extra a, .forumtitle a { color: #b94a48 !important; font-size: 12px; }
span.forumtitle a { color: #fff !important; }
#breadcrumb a:hover { text-decoration: underline !important; }
.newcontent_textcontrol, #quickreply_title, .forumoptiontitle, .blockhead, .forumhead { background: #b94a48 !important; border-radius: 0px !important; border: none !important; }
.threadlisthead { background: #b94a48 !important; border-radius: 0px !important; }
.searchlisthead { background: #b94a48 !important; border: none !important; }
span.threadinfo a { color: #fff !important; }
.newcontent_textcontrol:hover { text-decoration: underline !important; }
.threadbit { box-shadow: none !important; }
.forumdescription { margin-bottom: 5px !important; }
.blockbody, .blockrow, .blockfoot { background: #222 !important; border: none !important; }
.button { background: #b94a48 !important; border: none !important; color: #eee !important; }
.button:hover { text-decoration: underline !important; }
#cke_vB_Editor_QR_editor { background: #222 !important; border: none !important; }
.posthead, .threadinfohead { background: #b94a48 !important; border: none !important; }
.posthead { margin-left:0px !important; }
.postbitlegacy { border: 1px solid #222 !important; margin-bottom:3px; }
.postbody { border-left: 1px solid #222 !important; padding-top:10px !important; }
.signature { border-top: 1px solid #222 !important; }
h2.title { border-bottom: 1px solid #222 !important; color: #999 !important; margin-top:-10px; }
.bbcode_quote, .bbcode_quote_container { background: #222 !important; border: none !important; }
.bbcode_postedby, .message, .quote_container { color: #ccc !important; }
.bbcode_postedby strong { color: #eee !important; }
.newreply { background-color: #191919 !important; border: none !important; }
.quickreply, .newreply { color: #444 !important; }
.seperator { border-right: 1px solid #333 !important; }
.pagination span a { color: #ccc !important; background: #222 !important; border: 1px solid #333 !important; }
#showthread_navpopup .popupctrl, #showthread_navpopup .textcontrol, #forumdisplay_navpopup .popupctrl { background: #b94a48 !important; }
#forumdisplay_navpopup .textcontrol { background: #b94a48 !important; border: none !important; color: #ccc !important; }
.popupbody { background-color: #2d2d2d !important; border: 2px solid #b94a48 !important; }
.popupbody .formsubmit { background-color: #2d2d2d !important; }
.popupbody li { border: none !important; }
.popupbody li label { background: #2d2d2d !important; color: #fff !important; border-bottom: 1px solid #222 !important; }
.navpopupbody.popupbody li { background-color: #b94a48 !important; color: #fff !important; border: none !important; }
.popupbody li a { background: #2d2d2d !important; color: #fff !important; border-bottom: 1px solid #222 !important; }
.threadinfohead, .collapse.blockhead { background: #b94a48 !important; border: none !important; box-shadow: none !important; border-radius: 0px !important; }
.threadstats.td { width: 120px !important; font-size: 11px !important; }
.threadlastpost { font-size: 12px !important; }
.forumhead h2 { width: 87% !important; }
.forumhead .forumthreadpost { width: 170px !important; }
.forumhead .forumlastpost { width: 100px !important; }
.thread_info_block { color: #ccc !important; background: #222 !important; border: 1px solid #222 !important; box-shadow: none !important; }
#tag_edit_link { color: #eee !important; background: #b94a48 !important; border: none !important; }
#tag_edit_link:hover { text-decoration: underline !important; }
.popupctrl, #showthread_navpopup .textcontrol { color: #ccc !important; border: none !important; }
.forumrow, .nonsticky, .sticky { background-image: none !important; border-bottom: 1px solid #222 !important; border-top: 1px solid #444 !important; padding-top: 5px; padding-bottom: 5px; color: #ccc !important; }
.threadstats { background-image: none !important; }
.activitystream .popupmenu a.popupctrl { background: none !important; }
.popupbody.filter td, .popupbody.filter .unselected { background: #2b2b2b !important; border: none !important; }
.popupbody.filter .unselected { color: #ccc !important; }
.body_wrapper, .above_body { width: 1024px !important; margin-left:auto; margin-right:auto; }
.signaturecontainer, #wgo_onlineusers, .blocksubhead, #wgo_birthdays, #wgo_stats { font-size: 11px; }
.navbit, #breadcrumb { font-size: 11px; margin-bottom:0px !important; }
a.username strong { color: #eee !important; font-family: Tahoma !important; font-size: 11px !important; }
.lastposttitle { margin-bottom:5px; }
.floatcontainer { border: none; }
#navtabs .floatcontainer { border-top: 10px solid #b94a48 !important; background: #2d2d2d !important; -webkit-border-radius: 0px; border-radius: 0px; }
#navtabs .floatcontainer li { padding-right:35px !important; }
#wgo,#footer, .forumbit_post .forumrow,.forumbit_nopost .forumhead,.forumbit_nopost .forumrow,.forumbit_post .forumhead,.forumbits .forumhead, .threadlisthead, #forum_info, #forum_display_options, #forum_icon_legend, #posting_rules {-webkit-box-shadow:0px 0px 0px;box-shadow:0px 0px 0px; }
#wgo { margin-bottom:0px; }
#footer { margin-top:0px; }
.forumhead, .threadlisthead { border: none; border-top: 1px solid #191919; padding-right: 2px; padding-top: 3px; padding-bottom: 3px; margin-top:-1px; }
.collapse, .forumoptiontitle, .options_block_container .blockhead { padding-top: 3px; padding-bottom: 3px; margin-bottom:0px !important; }
.collapse img { margin-top: 3px; margin-right: 4px; }
.navbar_advanced_search li { padding-top: 10px !important; padding-right: 20px !important; } 
.customSelectBox .content { color: #eee !important; font-family: Tahoma !important; font-size: 11px !important; }
.customSelectBox .hider, .customSelectBox .front, .customSelectBox .back { background: #222 !important; }
.customSelectContainer { height:30px !important; padding: 0px !important; }
.forumactionlinks.td { display: none !important; }
.forumstats.td, .forumstats_2.td { width: 140px !important; margin-top: 10px !important; }
.forumstats li, .forumstats_2 li { font-size: 11px !important; }
.threaddetails.td { display: none !important; }
.lastpostby a strong, .threadlastpost a strong { color: #b94a48 !important; }
.threadbit .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_old-30.png) !important; }
.threadbit.hot .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_old-30.png) !important; }
.threadbit.hot.new .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_new-30.png) !important; }
.new .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_new-30.png) !important; }
.threadbit.dot .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_dot_old-30.png) !important; }
.threadbit.dot.hot .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_dot_old-30.png) !important; }
.threadbit.dot.new .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_dot_new-30.png) !important; }
.threadbit.dot.hot.new .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_dot_new-30.png) !important; }
.threadbit.dot.hot.lock .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_dot_lock_old-30.png) !important; }
.threadbit.dot.hot.lock.new .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_dot_lock_new-30.png) !important; }
.threadbit.dot.lock .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_dot_lock_old-30.png) !important; }
.threadbit.dot.lock.new .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_dot_lock_new-30.png) !important; }
.threadbit.hot.lock .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_lock_old-30.png) !important; }
.threadbit.hot.lock.new .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_lock_new-30.png) !important; }
.threadbit.lock .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_lock_old-30.png) !important; }
.threadbit.lock.new .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_lock_new-30.png) !important; }
.threadbit.moved .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_moved-30.png) !important; }
.threadbit.moved.new .threadstatus{background-image:url(https://dl.dropbox.com/u/4224739/9l/thread_moved_new-30.png) !important; }
.threadbit .deleted .threadstatus{background:url(https://dl.dropbox.com/u/4224739/9l/thread_deleted_30.png) !important; }
.rating1 .threadtitle, .rating2 .threadtitle, .rating3 .threadtitle, .rating4 .threadtitle, .rating5 .threadtitle { background-image: none !important; }
.forumicon { opacity: 0.8; }
