css:
@-moz-document url-prefix(http://football.fantasysports.yahoo.com/) {

  html {
    background-color: white;
  }

  .Thm-light .Thm-inherit .Table td.Selected, .Thm-light.Thm-override .Table td.Selected {
    background-color: #ffffe5;
  }

  .Thm-light .Thm-inherit .Table .alt .Selected, .Thm-light .Thm-inherit .Table .Alt .Selected, .Thm-light.Thm-override .Table .alt .Selected, .Thm-light.Thm-override .Table .Alt .Selected {
    background-color: #ffffe5;
  }

  .Thm-light .Thm-inherit.Mod, .Thm-light.Thm-override.Mod {
    box-shadow: none;
    border: 1px solid #cccccc;
  }


  #yucs-top-bar, #yucs {
    display: none;
  }
  
  #page-header {
    margin: 0 !important;
  }

  #fantasy .Rail:nth-child(2) {
    display:none;
  }
  
  #buzzindex {
    margin: 15px 0 0 0;
  }

  .Page-noexpand .RailSkyContent {
    width: 100%;
  }
  
  .RailSkyContent  {
    min-width: 100%;
  }

  .RailMain {
    width: 650px;
    margin: 0 auto;
  }

  #yspcontentfooter {
    display: none;
  }
}
