css:
@-moz-document url("http://webchat.freenode.net/?channels=tagpro") {
/*@import url(http://fonts.googleapis.com/css?family=Titillium+Web);
@import url(http://fonts.googleapis.com/css?family=Indie+Flower);
@import url(http://fonts.googleapis.com/css?family=Josefin+Sans);*/

.qwebirc-qui .lines {
  /*background:linear-gradient(to bottom right, black,lightgrey) !important;*/
  background:radial-gradient(grey,black) !important;
}

*, .qwebirc-qui .tab-selected {
  background:black !important;
  color:white !important;
  margin:2px !important;
  /*font-family:Titillium Web !important;*/
font-family:Arial !important;
}
    
.qwebirc-qui .ircwindow div, span, a {
  background:transparent !important;
        }

.qwebirc-qui .lines a {
  color:rgb(142, 255, 142) !important;
  text-shadow:0px 0px 5px lime;
transition:text-shadow 0.7s;
-webkit-transition:text-shadow 0.7s;
}
    
    .qwebirc-qui .lines a:hover {
transition:text-shadow 0.2s;
-webkit-transition:text-shadow 0.2s;
            text-shadow:0px 0px 15px white;
          }

.qwebirc-qui .input input.keyboard-input {
      font-family:Arial/*Josefin Sans*/ !important;
      font-size:15px !important;
      padding-left:6px !important;
    }
    
    .qwebirc-qui .ircwindow div {
          animation:fadein .5s;
          -webkit-animation:fadein .5s;
        }
        
        @keyframes fadein {
          from {opacity:0;}
          to {opacity:1;}
        }
        
        @-webkit-keyframes fadein {
       from {opacity:0;}
          to {opacity:1;}
        }

::selection {
  background:lightblue;
  text-shadow:1px 1px 3px black;
}
        
}
