body {background-color: #222222 !important;}

input, textarea, select {color: #FFFFFF;background-color: #222222;border-color: #2B2F37;-moz-appearance: none !important;}

body {color: #EAEAEA !important;background-color: #222222 !important;}

A.move:link {color: #FFFFFF !important;}

A.move:active {color: #FFFFFF !important;}

A.move:hover {color: #FFFFFF !important;}

A.move:visited {color: #FFFFFF !important;}

