A.moveOn:visited {color: #FFFFFF !important;background-color: #FF00FF !important;}

A.nor:link {color: #FFFF99 !important;}

A.nor:visited {color: #FFFF99 !important;}

A.nor:active {color: #FFFF99 !important;}

A.nor:hover {color: #66FFFF !important;}

a.whitewinheader  {border: solid 1px #000000; display: inline; z-index:-1; background-color:white;   margin: 0; text-align: center; color: #000000; border-right:none;}

a.drawheader      {border: solid 1px #828282; display: inline; z-index:-1; background-color:#828282; margin: 0; text-align: center; color: #FFFFFF; }

a.blackwinheader  {border: solid 1px #000000; display: inline; z-index:-1; background-color:black;   margin: 0; text-align: center; color: #FFFFFF; }

a.blackwinbox     {float:right; clear:none; margin: 0;text-align:center; vertical-align:middle; font-size:0.8em;position:relative;background-color:black;height:15px;border:1px solid #000;color:#FFFFFF;}

a.drawbox         {float:right; clear:none; margin: 0;text-align:center; vertical-align:middle; font-size:0.8em;position:relative;background-color:#828282;height:15px;border:1px solid #828282;border-right:none;border-left:none;color:#FFFFFF}

a.whitewinbox     {float:right; clear:none; margin: 0;text-align:center; vertical-align:middle; font-size:0.8em;position:relative;background-color:white;height:15px; border:1px solid #000;border-right: 1px solid #828282;color:#000000}

a.transparentbox  {float:right; clear:none; margin: 0;text-align:center; vertical-align:middle; font-size:0.8em;position:relative;background-color:white;height:15px;border:1px solid #FFF;color:#FFFFFF}

