a.ltviewer {color: #FFFFFF !important;}

a.ltviewer:hover {color: #222222 !important;background-color: #7F7F7F !important;}

#navi_top {color: #272727 !important;background-color: #222222 !important;}

#navi_top span {color: #999999 !important;}

#navi_top a {background-color: #111111 !important;color: #FFFFFF !important;}

#navi_top a:hover, #navi_bottom a:hover {background-color: #CCCCCC !important;color: #222222 !important;}

#navi_top ul, #navi_top ul li {color: #FFFFFF !important;}

#navi_top a {color: #FFFFFF !important;}

#navi_top .firstline {background-color: #222222 !important;background-image: url("data:image/png;base64,") !important;background-repeat: repeat-x !important;border-bottom-color: #292929 !important;}

#navi_top .secondline {background-color: #222222 !important;background-image: url("data:image/png;base64,") !important;background-repeat: repeat-x !important;border-bottom-color: #292929 !important;}

#navi_top .secondline a {color: #FFCC66 !important;}

#navi_top .secondline a:hover {color: #222222 !important;}

#navi_top .tline a {color: #FFCC66 !important;}

#navi_top .tline a:hover {color: #222222 !important;}

#tabmenu {color: #FFFFFF !important;}

#tabmenu a, a.active {color: #FFFFFF !important;background-color: #222222 !important;border-top-color: #FFFFFF !important;border-right-color: #FFFFFF !important;border-left-color: #FFFFFF !important;}

#tabmenu a.active {background-color: #1F1F1F !important;border-bottom-color: #1F1F1F !important;}

#tabmenu a:hover {background-color: #FFFFFF !important;color: #222222 !important;}

#tabmenu a.active:hover {background-color: #FFFFFF !important;color: #222222 !important;}

#content a:hover {background-color: #7674A1 !important;}

.result-table {border-top-color: #CCCCCC !important;border-right-color: #CCCCCC !important;border-bottom-color: #CCCCCC !important;border-left-color: #CCCCCC !important;color: #FFFFFF !important;}

.result-table a:hover {background-color: #D5B89C !important;}

.result-table th {border-top-color: #CCCCCC !important;border-right-color: #CCCCCC !important;border-bottom-color: #CCCCCC !important;border-left-color: #CCCCCC !important;background-color: #1F1F1F !important;}

.result-table td {border-top-color: #999999 !important;border-right-color: #999999 !important;border-bottom-color: #999999 !important;border-left-color: #999999 !important;color: #FFFFFF !important;}

.result-table tr.alt th, .result-table tr.alt td {color: #171717 !important;}

.result-table tr:hover td {background-color: #3F3F3F !important;}

#blink2 {background-color: #3F3F3F !important;}

.download-table {color: #FFFFFF !important;}

.stats-table {color: #FFFFFF !important;}

.stats-table th {border-top-color: #999999 !important;border-right-color: #999999 !important;border-bottom-color: #999999 !important;border-left-color: #999999 !important;background-color: #1F1F1F !important;}

.stats-table th.leftnoborder {border-top-color: #999999 !important;border-right-color: #999999 !important;border-bottom-color: #999999 !important;border-left-color: #999999 !important;}

.stats-table td {border-top-color: #999999 !important;border-right-color: #999999 !important;border-bottom-color: #999999 !important;border-left-color: #999999 !important;}

.stats-table td.rightboldborder {border-right-color: #999999 !important;}

.stats-table tr.alt th, .stats-table tr.alt td {color: #D5B89C !important;}

.currentfics-table {color: #FFFFFF !important;}

.eco-table {color: #FFFFFF !important;}

.move-table {color: #FFFFFF !important;}

.move-table a:link {color: #FFFF00 !important;}

.move-table a:visited {color: #FFFFFF !important;}

.move-table td {color: #FFFFFF !important;}

.move-table td.sanmove {color: #FFFFFF !important;}

.move-table td.boxdraw {border-top-color: #FFFFFF !important;border-right-color: #FFFFFF !important;border-bottom-color: #FFFFFF !important;border-left-color: #FFFFFF !important;color: #FFFFFF !important;}

.panelNode-html, .panelNode-stylesheet, .panelNode-css, .panelNode-html .sourceLine {background-color: #EEEEEE !important;}

.panelNode-html .highlighted .nodeLabel, .panelNode-html .mutated, .panelNode-html .mutated .nodeLabel {background-color: #CCCCCC !important;border-top-color: #AAAAAA !important;border-right-color: #AAAAAA !important;border-bottom-color: #AAAAAA !important;border-left-color: #AAAAAA !important;}

.panelNode-html .sourceLine {border-right-color: #CCCCCD !important;}

.panelNode-html .nodeTag {color: #06D98D !important;}

.panelNode-html .nodeLabelBox, .panelNode-html .nodeCloseLabel, .panelNode-html .textNodeBox, .panelNode-html .nodeText, .panelNode-html .sourceRowText, .panelNode-html .docType, .panelNode-css .cssHead, .panelNode-css .cssColon, .panelNode-css .insertBefore, .panelNode-css .cssSemi {color: #07070D !important;}

.panelNode-html .nodeBox.selected > .nodeCloseLabel .nodeTag, .panelNode-html .nodeBox.selected > .nodeLabel .nodeTag {border-bottom-color: #07070D !important;}

.panelNode-html .nodeName, .panelNode-css .cssSelector {color: #591DD1 !important;}

.panelNode-html .nodeValue {color: #001166 !important;}

.panelNode-html .twisty, .panelNode-html .selected .twisty {background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAAJCAYAAADgkQYQAAAAF0lEQVQYlWNgoCX4T7Gi/1gwDa2jDAAAyMsI+D/qQzYAAAAASUVORK5CYII=") !important;}

.panelNode-css .cssPropName {color: #992610 !important;}

.panelNode-css .cssPropValue, .panelNode-css .cssPropValueUsed, .panelNode-css .cssFontPropValue {color: #517E00 !important;}

.panelNode-css .objectLink {color: #06D98D !important;}

.panelNode-css .groupHeader {color: #8A8EA1 !important;border-top-color: #8A8EA1 !important;border-bottom-color: #8A8EA1 !important;}

.panelNode-css .groupHeader * {color: #8A8EA1 !important;}


