css:
@-moz-document domain("se7ensins.com") {
.img[src*="logo_bg"]{
    width:0;
    height:100px;
    padding-right:385px;
    background:transparent url(http://i.imgur.com/FAEFulZ.png) top left no-repeat;
}
.nodeList .categoryStrip {
background: linear-gradient(to bottom,rgba(280,159,8,1) 0%,rgba(166,99,22,1) 100%);
}
.navTabs {
background-color: #131313;    
border-bottom: 5px solid #F2A116;
}
.navTabs .navTab.PopupOpen {
background: linear-gradient(to bottom,rgba(166,99,22,1) 0%,rgba(280,159,8,1) 100%);
}
.navTabs .navTab.selected {
background: linear-gradient(to bottom,rgba(166,99,22,1) 0%,rgba(280,159,8,1) 100%);
}

.xenOverlay .formOverlay .heading {
background: linear-gradient(to bottom,rgba(32, 70, 18, 1) 0%,rgba(7, 14, 4, 1) 100%);
}
body > div,
body > div#content > div,
    .pageWidth {    
    max-width: 100% !important;
    box-sizing: border-box;
}   
    /* Member card stats and last activity in two columns Code provided by xGfcHoster */
    .xenOverlay.memberCard .userInfo .userStats {
    float: left;
    width: 49% !important;
    border-right: 1px solid #556655 !important;
    }

    .xenOverlay.memberCard .userInfo .userStats dd {
    float: right !important;
    }


    .xenOverlay.memberCard .userInfo .userStats dt  {
    float: left;
    clear: both;
    }

    .xenOverlay.memberCard .lastActivity {
    float: right;
    width: 49% !important;
    margin-top: 0px !important;
    padding-top: 0px !important;
    border-top: 0 !important;
    }

    .xenOverlay.memberCard .lastActivity.pairsInline dt {
    float: left;
    clear: both;
    }

    .xenOverlay.memberCard .lastActivity.pairsInline dd {
    float: center;
    clear: both;
}
}
