css:
/*changements :
	Page accueil mode compact : plus compact
	Commentaires : citations plus visibles
	News/Accueil : retour anciennes polices

*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nextinpact.com") {


    /*réglages text*/
    #color_title
    {
        font-size: 30px !important;
    }
    
    .unit-article h1 
    {
        font-size: 32px !important;
    }

    .unit-article .soustitre_article 
    {
        font-size: 12px !important;
    }
    
    .article_content, .actu_chapeau, p
    {
        font-family: Arial,Verdana !important;
    }
    
    #container_article 
    {
        font-size: 15px;
    }
    
    /*marges*/
    .actu-head
    {
        margin-top: 15px;
        margin-bottom: 30px;
    }
    
    p.actu_chapeau
    {
        margin-top: 35px;
    }
    
    h3
    {
        margin-top: 30px;
    }
    
    
    /*lettrine*/
    .actu_chapeau:first-letter, .bonplan-article div#container_article:first-letter, .longcontent-article div#container_article:first-letter, .first-letter-up:first-letter 
    {
        margin: 8px 3px 0px 0px !important;
        font-size: 37px;
    }
    
    .article_content h3 
    {
        font-size: 16px;
        font-family: Arial,Verdana !important;
    }
    
    
  /*centrage de corp principale*/
    #body 
    {
       padding-left: 8% !important;
    }
    

	.title_area h1, h2, h3, h4, h5, h6 {
  	font-family: Arial,Verdana !important;
  }
  
   /* ...en modes compact */
  .very-compact-item:nth-child(2n), .compact-item:nth-child(2n) { background: linear-gradient(to right, #121719, #171c1e) !important }
  
  .very-compact-item:hover, .compact-item:hover { background: #00232F !important }
  
  .compact-item { padding-bottom: 4px !important }

 .color_title a {
  	color: #FFFFFF;
   font-size:17px;
    }


/*haut de la page news*/

#page-container {
margin-top: 0px;
}

#container_hot_social {
  display : None;
  }

#header_list_news {
height: 30px;
margin-top: 0px;
}


/*Affichage des commentaires*/

#global_comment {
background-color: #1F2528;
}

.text_comment {
color: #FFFFFF;
  
}

.quote_bloc {
    background-color:  #2a3135 !important;
    border-left: 3px solid #ccd8dc !important;
    margin-bottom: 2px !important;
    margin-top: 5px !important;
  
  }

.qcontent {
	color: #ccd8dc !important;
  	padding: 0px 10px 5px !important;
}

.left_part_comment {
    border-bottom: 2px solid #797d7f;
    border-left: 2px solid #797d7f;
    margin-bottom: 7px;
}
  
.content_post {
	padding: 10px 0px 15px;
}

#post_comment_area
	.commentaire_avatar {
  border: 2px solid #FFFFFF;
height: 80px !important;
line-height: 80px !important;
width: 80px !important;
}

/* Mise en forme pour avoir les lettres en icône */ 
/*Stylish original par Zéfling : https://userstyles.org/styles/104381/next-inpact-v6-compact-ultra-compact*/



.very-compact-item div.image_area > a {
    height: 43px;
    width: 43px;
    display:inline-block;
    border:0;
}
  
.very-compact-item div.image_area > a:after{
   font-family:"Segoe UI Symbol", "Ubuntu Condensed","Segoe UI","Verdana","Helvetica",sans-serif;
   content: "N";
   color:white;
   font-size:30px;
   line-height:43px;
   width:38px;
   text-align:center;
   display:inline-block;
   padding: 0;
   margin-top: -3px;
} 

.very-compact-item a.droit-border {
  background-color: #E74F4F;
}
.very-compact-item div.image_area > a.droit-border:after {
  content: "\2696"; 
}
.very-compact-item a.blog-border, 
.very-compact-item a.next-inpact-border {
    background-color: #EA8212;
}

.very-compact-item div.image_area > a.blog-border:after {
  content: "\1F481"; 
}

.very-compact-item div.image_area > a.next-inpact-border:after {
  content: "\25B6"; /* Next Inpact */
}

.very-compact-item a.mobilite-border {
    background-color: #AF67B1;
}
.very-compact-item div.image_area > a.mobilite-border:after {
  content: "\1F4F1"; 
}
.very-compact-item a.culture-numerique-border {
    background-color: #89571C; 
}
.very-compact-item div.image_area > a.culture-numerique-border:after {
  content: "\1F3AE"; 
}
.very-compact-item a.internet-border {
    background-color: #00C1E8;
}
.very-compact-item div.image_area > a.internet-border:after {
  content: "\1F4E1";
}
.very-compact-item a.materiel-border {
    background-color: #1CC38A;
}
.very-compact-item div.image_area > a.materiel-border:after {
  content: "\1F4BB";
}
.very-compact-item a.economie-border {
    background-color: #B1B1B1;
}
.very-compact-item div.image_area > a.economie-border:after {
  content: "\1F4C8";
}
.very-compact-item a.logiciel-border {
    background-color:  #167DAC;
}
.very-compact-item div.image_area > a.logiciel-border:after {
  content: "\2328";
}
  
.compact_mode .very-compact-item .title_area {
    width: 793px;
}  

/* image plus petite*/
#container_main_image_article, #container_main_image_article > img
  {
    width: 423px !important;
    height: 205px !important;
    margin-left: auto !important;
    margin-right: auto !important;
  }


#widgetLidd .btn_social_lidd {
  display : None;  
}

#widgetLidd .content_lidd_item a:visited {
  color : #b58adb    
}


#widgetLidd .content_lidd_item a{
  color : #69BFE7
}
.icon-info-content {
display: None;
}
.title_area a {
	margin-left:5px;
}
}
