css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tweetdeck.twitter.com") {
/*******************************************************
REMOVE SCROLLBARS
*******************************************************/
.js-column-scroller {
    width: 100% !important;
}
/*******************************************************
COLUMN HEADER
*******************************************************/
div.column-content {
    top: 30px!important;
}
.column-header, 
.column-header-temp {
    height: 30px!important;
    line-height: 29px!important;
}
.sprite-drag-vertical {
    height: 14px!important;
}
.column-type-icon {
    display: none;
}
.column-title {
    padding: 0 0 0 7px!important;
    font-size: 17px!important;
}
.txt-ellipsis {
    text-overflow: clip !important;
}
/*******************************************************
COLUMN HEADER - @NAME
*******************************************************/
.txt-sub-antialiased {
    display: none;
}
/*******************************************************
TWEETS
*******************************************************/
.column {
    margin: .5px!important;
    font-size: 12px!important;
    width: 49.7% !important;
	}
html,
.tweet-body {
    line-height: 1.1!important;
}
.item-box {
    padding: 3px 5px !important;
}
/*******************************************************
TWEETS - NAME (This makes it so you only see @username. Delete it if you want them.)
*******************************************************/
.fullname {
    display: none !important;
    margin: 0 20px 0 0!important
}

.username {
    color: #C5C5C5 !important;
    font-size: 13px !important;
    font-weight: bold !important;
    margin: 0 0 0 0px !important;
}
/*******************************************************
TWEETS - Reply, Etc. Icons
*******************************************************/
.icon-reply,
.icon-retweet,
.icon-favorite,
.icon-more {
    font-size: 15px !important;
}
.icon-protected {
    margin:-2px 0 0;
}
.icon-conversation {
    margin: 5px 0 0px !important;
}
/*******************************************************
TWEETS - User Icons (Remove border-radius: 50px !important; to get rid of rounded icons)
*******************************************************/
.avatar {
    border-radius: 50px !important;
    height: 50px !important;
    margin: 0px -5px !important;
    width: 50px !important;
    border: 3px solid #404246;
}
/*******************************************************
COMPOSE
*******************************************************/
.compose-header {
    height: auto;
}
.compose-account-img {
	width: 49px !important;
	height: 49px !important;
	margin: 0 !important;
	padding:0px!important;
	border-radius: 0px !important;
}

}
