/* Strip unwanted bgs */

* {
	background-color: rgba(0,0,0,0) !important;
}
/* remove esports BG */
html body .backdrop .header {
	background-image: none !important;
}

* {
	color: #fff !important;
}


/* strip borders */
div.box,
table,
tr,
td,
div.redtracker-list-item div.row,
div.masthead.row,
div#social-bar.row,
header.header,
.plupload_filelist_header,
.plupload_droptext,
li.plupload_done,
div#riot-apollo-messaging-modal div, 
div#riot-apollo-messaging-modal li,
div#my-updates div.comment-list-item {
	border: 0 !important;
}

/* Add Akali to BG and style */
body {
	background-color: #eaeaea !important;
	background-image: url("http://i.imgur.com/r5fAcUg.jpg") !important;
	background-attachment: fixed !important;
	background-repeat: no-repeat !important;
	background-position: right top !important;
	background-size: cover !important;
}



