div#comments h3,
div.reply,
div#DropDown-champion,
div#DropDown-summoner,
div#DropDown-item ,
div#uploader,
div#rg-commonbox-tooltip,
div.riot-apollo.profile-hover,
div.pager,
div#profile-widget,
div#rioter-comments-widget,
div.modal-body,
div.comment.clearfix,
div.empty-state,
div.report-widget,
