  opacity:1 !important;
}

/* sprite editing */
.loading-indicator .fist,
.loading-indicator .halo,
hr,
.inline-profile .icon img,.inline-profile .icon .riot-fist,
div.search-icon,
.attachments a.delete-attachment,
.apollo-header .breadcrumb li.arrow,
#communities-navigation-container .communities-navigation-list li.redtracker-community .riot-fist,
#rioter-comments-widget .divider .glove .riot-fist,
#rioter-comments-widget .controls a.previous span,
#rioter-comments-widget .controls a.next span,
.dropdown a.btn span em.icon-arrow-down,
#discussion .oembed-content-block .link-reference span.icon-link-share,
#discussion .masthead .discussion-title h1.title span.icon-external-link,
#discussion #social-bar .buttons a.twitter,
#discussion #social-bar .buttons a.facebook,
.comments-header-container .flat-rioter-comments .riot-fist,
#comments span.icon-external-link,
#comments .reply.form-container .cant-comment-warning span.icon-lock-brown,
.discussion-list-item .voting .pin,
.discussion-list-item .thumbnail,
.discussion-list-item .title .discussion-title span.icon-link-share,
.discussion-list-item .title .discussion-title a.icon-external-link,
.discussion-list-item .riot-commented .riot-fist,
.nested-comment a.toggle-minimized,
.nested-comment .body-container .header .flat-rioter-comment-tracker a.prev,
.nested-comment .body-container .header .flat-rioter-comment-tracker a.next,
.nested-comment .body-container .header .flat-rioter-comment-tracker a.last,
.nested-comment .body-container .header .flat-rioter-comment-tracker .riot-fist,
.nested-comment .body-container .body .video-thumb-link .oembed-video-icon,
.nested-comment .paging a span.bubble,
.nested-comment.isMinimized a.toggle-minimized,
.reply.form-container .controls li a.upload,.edit.form-container .controls li a.upload,
.comment-list-item .inline-profile .icon .riot-fist,
#header #header-content a#lol-home-logo,
#submit .oembed-content-block .link-reference span.icon-link-share,
#submit .form-container .bottom-bar .upload-toggle,
#submit .box.collapseable .toggle .minimize,
#red-tracker .redtracker-list-item .row .riot-commented .riot-fist,
body .content .rfmtoolbar .buttons .button.bold,
body .content .rfmtoolbar .buttons .button.italic,
body .content .rfmtoolbar .buttons .button.strikethrough,
body .content .rfmtoolbar .buttons .button.url,
body .content .rfmtoolbar .buttons .button.blockquote,
body .content .rfmtoolbar .buttons .button.unordered,
body .content .rfmtoolbar .buttons .button.champion,
body .content .rfmtoolbar .buttons .button.ordered,
body .content .rfmtoolbar .buttons .button.summoner,
body .content .rfmtoolbar .buttons .button.item,
#uploader .plupload_content .plupload_file_action a,#uploader .plupload_content .plupload_file_action span
{
	background-image: url("https://i.imgur.com/AyxG2WM.png") !important;
}

div.information-container div.navigation a.updates-count {
	background-image: url("https://i.imgur.com/Dhh3o5j.png") !important;
}

div.information-container div.navigation div.messaging a {
	background-image: url("https://i.imgur.com/znpu6V6.png") !important;
}

/* footer formatting */
div#footer {
	border-radius: 0 !important;
}

div.guidelines {
	padding: 10px !important;
}


/* fix up profile/new pages */
table#player-profile.box.main {
	border: 0 !important;
	background-image: none !important;
	box-shadow: 0 0 0 !important;
}

table#player-profile.box.main tr.discussion-list-item.row {
	height: 100% !important;
}

/* profile post box */
table#player-profile.box.main tr.comment-row {
}

div.parent-comment  {
	opacity: 1 !important;
}

/* profile post box */
table#player-profile.box.main tr.comment-row td {
	margin: 10px 0 10px 0 !important;
	width: 100% !important;
	display: table !important;
}

/* style post embed box */
div.oembed-content-block {
	border: 0 !important;
	border-radius: 25px !important;
	background-color: rgba(0,0,0,.4) !important;
	box-shadow: 0 0 6px rgba(0,0,0,.8) !important;
}

/* quotes */
blockquote {
	border-left: 2px solid #431618 !important;
	font-size: .9em !important;
	margin: 5px 5px 5px 15px !important;
	padding: 15px 15px 0 15px !important;
	font-style: italic !important;
	background: url("http://i.imgur.com/wBl2AVQ.png") !important;
	background-repeat: no-repeat !important;
}

blockquote p:last-child {
	background-image:url("http://i.imgur.com/5uGnFVf.png") !important;
	background-repeat: no-repeat !important;
	background-position: bottom right !important;
	padding: 0 0 15px 0 !important;
}

/* strip Boards BG images */
div.header,
div.magic {
	background-image: none !important;
}

/* top nav bar */
ul.breadcrumb {
	position: fixed !important;
	top: 0 !important;
	left: 0 !important;
	z-index: 12 !important;
	width: 100% !important;
	height: 25px !important;
	border-radius: 0 !important;
	border-width: 0 0 3px 0 !important;
	margin: 0 0 50px 0 !important;
	padding: 4px 400px 4px 4px !important;
	overflow: hidden !important;
}

/* top search/info box */
div.column.side form#search-form  {
	position: fixed !important;
	top: 4px !important;
	right: 3px !important;
	z-index: 13 !important;
	width: 250px !important;
	border: 0 !important;
}

span#player-profile-mini {
	border: 0 !important;
	position: fixed !important;
	top: 0 !important;
	right: 0 !important;
	width: 385px !important;
	z-index: 12 !important;
	padding: 5px 5px 5px 15px !important;
	border-radius: 0 !important;
	border-bottom-left-radius: 25px !important;
}

/*	Top buttons */
div.pvpnet-id-card a.link-1,
div.pvpnet-id-card a.link-2,
div.pvpnet-id-card a[href="http://support.leagueoflegends.com/"] {
	position: absolute !important;
	right: 0 !important;
	width: 150px !important;
	height: 35px !important;
	border-radius: 0 !important;
	text-align: center !important;
	box-shadow: none !important;
}

div.pvpnet-id-card a.link-1 {
	top: 132px !important;
	border-bottom-right-radius: 25px !important;
	border-bottom-left-radius: 25px !important;
}

div.pvpnet-id-card a.link-2 {
	top: 97px !important;
}

div.pvpnet-id-card a[href="http://support.leagueoflegends.com/"] {
	top: 62px !important;
}

/* fix search while logged out */
div.pvpnet-id-card a.link-1[href*="account.leagueoflegends.com/login"] {
	border: 0 !important;
	display: block !important;
	position: fixed !important;
	top: 10px !important;
	right: 0 !important;
	width: 350px !important;
	z-index: 12 !important;
	padding: 10px 5px 5px 25px !important;
	border-radius: 0 !important;
	border-bottom-left-radius: 25px !important;
	text-align: left !important;
	margin: 0 0 -35px 0 !important;
}


/* headers */
div#header {
	margin: 0 0 -100px 0 !important;
	position:relative !important;
	z-index: 11 !important;
	border-radius: 0 !important;
}

div.pvpnet-region-switcher div.current-region {
	margin: 25px 0 0 0 !important;
}

a#lol-home-logo {
	margin: 20px 0 0 0 !important;
} 

div.apollo-header div#breadcrumbs {
	margin: -20px auto 0 auto !important;
	max-width: 1360px !important;
	padding: 10px !important;
}
/********************/


/*	left box tweaks */
li.redtracker-community.clearfix a.red-tracker span{
	margin: 0 0 20px 0 !important;
}

/* comment chain starter */
div.nested-comment.box.clearfix.glow {

}

/* op reply */
div.nested-comment.box.clearfix.isOP  {

}

/* subcomments alternate*/
.nested-comment,
.nested-comment .nested-comment .nested-comment,
.nested-comment .nested-comment .nested-comment .nested-comment .nested-comment,
.nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment,
.nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment ,
.nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment, 
.nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment,
.nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment,
.nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment,
.nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment {
	background-image: url("http://i.imgur.com/gmclENx.jpg") !important;
	background-attachment: fixed !important;
	background-repeat: no-repeat !important;
	background-position: right top !important;
	background-size: cover !important;
}

.nested-comment .nested-comment,
.nested-comment .nested-comment .nested-comment .nested-comment,
.nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment ,
.nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment ,
.nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment,
.nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment,
.nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment,
.nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment,
.nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment .nested-comment,
.nested-comment div.reply.form-container.box {
	background-color: rgba(0,0,0,.4) !important;
	background-image: none !important;
}



/*	Board topic hover */
div.discussions > table > tbody > tr,
ul.dropdown-menu a,
div.redtracker-list-item ,
span.show-more-label,
div.paging.right a.btn-large.btn-large-secondary span,
div.main.box.glow#submit form#submission-form div.toggle {
	transition: background .3s !important;
}

div.discussions > table > tbody > tr:hover,
ul.dropdown-menu a:hover,
div.redtracker-list-item:hover,
span.show-more-label:hover,
div.paging.right a.btn-large.btn-large-secondary span:hover,
div.main.box.glow#submit form#submission-form div.toggle:hover {
	background: rgba(0,0,0,.5) !important;
}

div.main.box.glow#submit form#submission-form div.toggle {
	border-radius: 25px !important;
}

/* show more boxes fix */
a.box.show-more {
	box-sizing: border-box !important;
	height: 50px !important;
}

span.show-more-label,
div.paging.right a.btn-large.btn-large-secondary span{
	width: 102% !important;
	height: 100% !important;
	display: block !important;
	border-radius: 25px !important;
	height: 50px !important;
	margin: -10px !important;
	vertical-align: middle !important;
	padding: 13px 0 0 0 !important;
}

div.paging.right a.btn-large.btn-large-secondary span{
	margin: -15px 15px -15px 0 !important;
	display: block !important;
	width: 100% !important;
}

div.paging.right a.btn-large.btn-large-secondary {
	display: block !important;
	margin: 0 0 0 -10px !important;
	width: 102% !important;
}

div.paging.right a.btn-large.btn-large-secondary span.bubble {
	display: none !important;
}

div.paging.right {
	border: 0 !important;
	outline: 0 !important;
	position: relative !important;
	z-index: 1 !important;
}

span.show-more-label:hover {
	background: rgba(0,0,0,.5) !important;
}

/*	make sure the hover background doesn't clip over the table's rounded borders   */
tr:first-child,
div#red-tracker div.redtracker-list-item:nth-child(2) {
	border-top-left-radius: 25px !important;
	border-top-right-radius: 25px !important;
}

tr:last-child,
div#red-tracker div.redtracker-list-item:last-child {
	border-bottom-left-radius: 25px !important;
	border-bottom-right-radius: 25px !important;
}
/***********************/


/* dropdown menu/hover box formatting */
ul.dropdown-menu,
div.nav-dropdown-container,
div#DropDown-champion,
div#DropDown-summoner,
div#DropDown-item,
div.riot-apollo.profile-hover,
div.report-widget,
div#rg-commonbox-tooltip,
div#toolbar-formatting-guide.modal.riot-component,
div#pvpnet-login-container,
ul.region-selector,
div#oembed-content-modal,
div#riot-apollo-messaging-modal.modal.player-messaging.box,
div#account-management-container,
div.language-selector,
#styled-dropdown-button-application_id > span,
#styled-dropdown-button-created_from > span
{
	border: 1px solid rgba(255,255,255,.4) !important;
	border-radius: 15px !important;
	box-shadow: 0 0 5px rgba(255,255,255,.3) !important;
	opacity: .92 !important;
}

div.riot-apollo.profile-hover div.arrow {
	display: none !important;
}

iframe.formatting-guide-iframe {
	border-radius: 15px !important;
}

div#riot-apollo-messaging-modal.modal.player-messaging.box.main.riot-component {
	border-radius: 25px !important;
}

div#riot-apollo-messaging-modal.modal.player-messaging div.glow {
	display: none !important;
}

div.column.main#page-main div#submit.main.box.glow input {
	border: 0 !important;
}
/*********************/




/* up and downvotes */
.voting ul.riot-voting li .up-vote {
	background-image: url("http://i.imgur.com/3wyQzhq.png") !important;
	background-repeat: no-repeat !important;
	background-position: 0 0 !important;
	width: 22px !important;
	height: 16px !important;
	transition: opacity 1s !important;
}

.voting ul.riot-voting li .up-vote:hover {
	background-position: 0 -16px !important;
}

.voting ul.riot-voting li .up-vote:active {
	background-position: 0 -32px !important;
} 

.voting ul.riot-voting li .down-vote {
	background-image: url("http://i.imgur.com/nLf5C8l.png") !important;
	background-repeat: no-repeat !important;
	background-position: 0 0 !important;
	width: 22px !important;
	height: 16px !important;
	transition: opacity 1s !important;
}

.voting ul.riot-voting li .down-vote:hover {
	background-position: 0 -16px !important;
}

.voting ul.riot-voting li .down-vote:active {
	background-position: 0 -32px !important;
} 

.voting ul.riot-voting.upVoted li .up-vote {
	opacity: 1;
}

.voting ul.riot-voting.upVoted li .down-vote {
	opacity: .2;
}

.voting ul.riot-voting.downVoted li .up-vote {
	opacity: .2;
}

.voting ul.riot-voting.downVoted li .down-vote {
	opacity: 1;
}
/******************/




/* 		table fixes    */
/* restructure the table */
td.title  {
	width: 100%  !important;
}

td.num-comments.byline,
td.view-counts.byline {
	padding: 0 15px 0 15px !important;
	min-width: 80px !important;
}

/* force table cells to valign properly */
tr.discussion-list-item.row  {
	vertical-align: middle !important;
	display: table !important;
}
/********************************/


/* fix new comments link */
a.new {
	width: 100% !important;
	margin: 0 !important;
	padding: 0 !important;
}

/* style posted images */
div.body img.oembed,
div.attachments a.attachment.lightbox {
	border: 1px solid #1a0909 !important;
	box-shadow: 0 0 6px rgba(26,9,9,.8);
}

/* pad out the text */
div#comments h3 {
	padding: 10px !important;
	margin: 0 -10px 0 -10px !important;
}

/* fix up the reply area */
div.reply {
	padding: 50px 20px 20px 20px !important;
}

div.reply textarea {
}

/* extra padding for user info hover box and profile page user info box */
div.riot-apollo.profile-hover,
div#profile-widget {
	padding: 10px !important;
}

/* page numbers */
div.pager {
	padding-left: 25px !important;
	padding-right: 25px !important;
}

div.pager a {
	padding: 15px !important;
	border: 0 !important;
}

div.pager a.active {
	font-weight: bold !important;
	background-color: rgba(0,0,0,.5) !important;
}

/* style red posts */
div.nested-comment.box.isRioter {
	border-top: 2px solid rgba(174,37,15,.75) !important;
}

div.triangle {
	border: 0 !important;
	background: url("http://i.imgur.com/OzB3jfI.png") no-repeat !important;
	width: 373px !important;
	height: 100% !important;
	z-index: 0 !important;
	opacity: .15 !important;
}

div.nested-comment.box.isRioter p,
div.flat-rioter-comment-tracker {
	position: relative !important;
	z-index: 1 !important;
}

div.riot-apollo.profile-hover.rioters {
	box-shadow: 0 0 5px rgba(174,37,15,.3) !important;
	border: 1px solid rgba(174,37,15,.5) !important;
}

/* make sure red posts are reportable!!! */
a.report-link {
	position: relative !important;
	z-index: 1 !important;
}
/****************************/


/* style reply area boxes */
div.comments-header-container {
	padding: 10px !important;
	margin: 60px 0 10px 0 !important;
}


/* color input areas white so that they look like input areas */
.button,
button#cboxClose,
a#uploader_browse.plupload_button {
	border-radius: 15px !important;
