css:
@-moz-document url-prefix(http://boards.na.leagueoflegends.com/) {
 #FEKquicklist {
	padding-left:7px; padding-top:7px;
} #FEKquicklist ul {
	position:relative;
	display:none;
	white-space:nowrap;
	padding:0px; padding-top:4px; padding-bottom:4px;
	margin:0px;
	border:1px solid #59574d;
	background:rgba(0, 0, 0, .85);
	-webkit-box-shadow: 0px 0px 5px rgba(255, 255, 255, 0);
	box-shadow: 0px 0px 5px rgba(255, 255, 255, 0);
} #FEKquicklist ul li {
	position:relative;
	z-index:20;
	background:rgba(0, 0, 0, .05);
	list-style:none;
	display:block;
	padding:0px;
	margin:0px;
} #FEKquicklist ul li a:link, #FEKquicklist ul li a:visited {
	text-decoration:none;
	text-align:center;
	text-transform:uppercase;
	display:block;
	color:#6dc0cd !important;
	font:10px Spiegal Regular, sans-serif;
	font-family: "Spiegel Regular",sans-serif;
	letter-spacing:1px;
	padding:6px;
	padding-left:6px;
	padding-right:6px;
	transition:color .3s;
} #FEKquicklist ul li a:hover {
	text-decoration:underline;
} .inline-profile .icon #FEKquicklist {
	z-index:1001;
	position:absolute; top:0px; left:0px;
	opacity:0;
	width:0px;
	transition:opacity .6s, width .6s;
	transition-timing-function: ease;
} .inline-profile .icon:hover #FEKquicklist {
	opacity:1;
	width:135px;
} .inline-profile .icon:hover #FEKquicklist ul {
	z-index:6;
	display:block;
} #fektooltip {
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0.85);
    border: 1px solid #59574d;
		border-radius: 0px;
	  box-shadow: 0 0 0px #000000;
    color: #ffffff;
    font-size: 12px;
    max-width: 250px;
    opacity: 0;
    padding: 8px;
    position: absolute;
    text-shadow: 0px 0px rgba(0, 0, 0, 0);
    transition: opacity 0.2s ease 0s;
    word-wrap: break-word;
    z-index: 999999;
    font-family: "Spiegel Regular",sans-serif;
    letter-spacing: 1px;
}
#fektooltip #ttlabel {
    color: #6dc0cd;
	  font-weight: normal;
}
#fektooltip #loadtime {
    font-size: 10px;
    padding-top: 5px;
    color: #cab5a2;
}
#fektooltip p {
    margin: 4px 0 0;
    padding: 0;
    color: #E6D9CF;
} #fekpanel {
    display: none;
    height: 100vh;
    left: 0;
    position: fixed;
    top: 0;
    z-index: 5000;
}
#fekpanel #col1 {
    background:rgb(20, 20, 20);
    height: 100%;
    margin-top: 0;
    padding-top: 20px;
    position: fixed;
    top: 0;
    width: 165px;
    z-index: 2001;
    border-left: 1px solid #59574d;
}
#fekpanel #col2 {
    background:rgb(3, 3, 3);
    height: 100%;
    margin-top: 0;
    position: fixed;
    top: 0;
    width: 520px;
    z-index: 2000;
    border-left: 1px solid #59574d;
    border-right: 1px solid #59574d;
}
#fekpanel #logo {
    height: 36px;
    margin: auto;
    width: 79px;
}
#fekpanel #version {
    color: #6dc0cd;
    font-family: "Spiegel Regular",sans-serif;
	  font-size: 12px;
	  letter-spacing: 1px;
    position: absolute;
    right: 4px;
    top: 4px;
}
#fekpanel #tabs h1 {
    border-bottom: 0px solid #6dc0cd;
    color: #6dc0cd;
    cursor: default;
    font-family: "Spiegel Regular",sans-serif;
	  font-size: 12px;
	  letter-spacing: 1px;
    margin: 20px auto 8px;
    padding: 0;
    text-align: center;
    text-decoration: none;
    width: 80%;
}
#fekpanel #tabs #tabgroup {
    cursor: pointer;
}
#fekpanel #tabs #tab {
    background: none;
	  border-top: 1px solid #59574d;
	  border-bottom: 1px solid #59574d;
    color: #cab5a2;
    display: block;
    font-family: "Spiegel Regular",sans-serif;
	  font-size: 12px;
	  letter-spacing: 1px;
    margin: -1px auto auto;
    padding: 7px 0 8px;
    position: relative;
    text-align: center;
    text-decoration: none;
    transition: background 0.5s ease 0s, color 0.5s ease 0s;
    width: 100%;
}
#fekpanel #tabs #tab.active, #fekpanel #tabs #tab.active:hover {
    background: none repeat scroll 0 0 rgba(2,27,32,.85);
    color: #E6D9CF;
    transition: background 0s ease 0s, color 0s ease 0s;
}
#fekpanel #tabs #tab:hover {
    background:rgba(85,85,85,.85);
    color: #cab5a2;
}
#fekpanel #tabs #tab #indicator {
    background: none repeat scroll 0 0 #393939;
    display: none;
    height: 20px;
    position: absolute;
    right: -10px;
    top: 4px;
    transform: rotate(45deg);
    transition: background 0.5s ease 0s;
    width: 20px;
}
#fekpanel #tabs #tab.active #indicator, #fekpanel #tabs #tab.active:hover #indicator {
    background: none;
    display: none;
}
#fekpanel #tabs #tab:hover #indicator {
    background: none repeat scroll 0 0 #595959;
}
#fekpanel #contentview {
    display: none;
    left: 0;
    padding-bottom: 170px;
    position: absolute;
    top: 0;
    width: 100%;
}
#refreshNotice {
	z-index:3000;
	cursor:pointer;
	position:absolute; top:-50px; left:0px; right:0px;
	background:rgba(30,30,30,.85);
	border-bottom: 1px solid #59574d;
	padding:10px;
	font-family: "Spiegel Regular",sans-serif;
	font-size: 12px;
	letter-spacing: 1px;
	font-weight:normal;
	text-align:center;
	color:#cab5a2;
	transition: top .2s, background .2s;
} #refreshNotice.visible {
	top:0px;
} #refreshNotice:hover {
	background:rgba(2,27,32,.85);
	color:#E6D9CF;
} #fekpanel #col2 .breakhead {
	clear:both;
} #fekpanel #contentview a:link {
	color:#6dc0cd; /* NEW: Specify a color so we don't get bad inherited colors */
	text-decoration:none;
} #fekpanel #contentview a:visited {
	color:#6dc0cd; /* NEW: Specify a color so we don't get bad inherited colors */
	text-decoration:none;
} #fekpanel #contentview a:hover {
	color:#6dc0cd; /* NEW: Specify a color so we don't get bad inherited colors */
	text-decoration:underline;
} #fekpanel #contentview a:active {
	color:#6dc0cd; /* NEW: Specify a color so we don't get bad inherited colors */
	text-decoration:underline;
} #fekpanel #contentview {
	color:#cab5a2; /* NEW: Specify a color so we don't get bad inherited colors */
	padding-right:20px;
	font-family: "Spiegel Regular",sans-serif;
	font-size: 12px;
	letter-spacing: 1px;
} #fekpanel #contentview h1 {
	clear:both;
	font-family: "Spiegel Regular",sans-serif;
	font-size: 16px;
	letter-spacing: 1px;
	font-weight: normal;
	padding: 5px;
	margin-bottom: 8px; margin-top:15px;
	color: #6dc0cd;
	border-bottom:1px solid #59574d;
} #fekpanel #contentview pre {
    white-space: pre-wrap;
} #fekpanel #contentview #hotkey {
	display:inline-block;
	min-width:100px;
	max-width:100px;
	text-align:center;
	padding:5px;
	margin-right:10px; margin-left:10px;
	background:rgba(2,27,32,.85);
	color:#cab5a2;
	border-radius: 0px;
	-webkit-box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.9);
	-moz-box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.9);
	box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.9);
	text-shadow:0px 0px rgba(0,0,0,.8);
	border:1px solid #59574d;
	font-family: "Spiegel Regular",sans-serif;
	font-size: 14px;
	letter-spacing: 1px;
	font-weight:normal;
} #fekpanel #button {
	z-index:301;
	position:relative;
	float:left;
	cursor:pointer;
	width:210px; height:33px;
	font-family: "Spiegel Regular",sans-serif;
	font-size: 11px;
	letter-spacing: 1px;
	padding-left:40px; margin-right:12px; margin-bottom:12px;
	border: 1px solid #59574d;
	color:#E6D9CF;
	background:rgba(2,27,32,.85);
	transition: background .3s;
	
	/* Internet Explorer 10 */
	display:-ms-flexbox;
	-ms-flex-pack:left;
	-ms-flex-align:center;

	/* Firefox */
	display:-moz-box;
	-moz-box-pack:left;
	-moz-box-align:center;

	/* Safari, Opera, and Chrome */
	display:-webkit-box;
	-webkit-box-pack:left;
	-webkit-box-align:center;

	/* W3C */
	display:box;
	box-pack:left;
	box-align:center;
	
} #fekpanel #button:hover {
	z-index:8000;
} #fekpanel #button #fektooltip-data {
	display:none;
}  #fekpanel #button #indicator {
	position:absolute; left:0px; top:0px;
	height:31px; width:33px;
	border-right:1px solid #59574d;
	background:rgba(2,27,32,.85);
	transition: background .3s;
} #fekpanel #button:hover {
	background:rgba(12,72,84,1);
}

#fekpanel #button.inactive {
	background:rgba(30,30,30,1);
} #fekpanel #button.inactive #indicator {
	background:rgba(30,30,30,1);
} #fekpanel #button.inactive #indicator-drop {
	background:rgba(30,30,30,1);
} #fekpanel #button.inactive:hover {
	background:rgba(85,85,85,1);
} #fekpanel #button.inactive #choice {
	color:#c9c9c9;
}

#fekpanel .dropdown ul {
	display:none;
	max-height:110px;
	overflow:auto;
	background:rgba(12,72,84,1);
	color:#539EAD;
	position:absolute; left:0px; right:0px; top:22px;
	border: 1px solid #59574d;
	padding:0px;
} #fekpanel .dropdown ul li {
	padding:6px;
	list-style:none;
} #fekpanel .dropdown ul li:hover {
	color:#E6D9CF;
	background:rgba(2,27,32,1);
} #fekpanel .dropdown #label {
	position:absolute; top:2px;
} #fekpanel .dropdown #choice {
	position:absolute; top:16px;
	color:#539EAD;
} #fekpanel .inactive ul {
	background:rgba(85,85,85,1);
	border: 1px solid #59574d;
} #fekpanel .inactive ul {
	color:#c9c9c9;
} #fekpanel .inactive ul li:hover {
	color:#E6D9CF;
	background:rgba(30,30,30,1);
}

#fekpanel #col2 .fekScrollRegion {
	position:absolute;
	top:20px; right:0px; bottom:0px; left:0px;
	margin-left:20px; margin-right:10px; margin-top:20px; margin-bottom:41px;
	padding:0px;
	overflow:auto;
}

#fekalert {
	border:1px solid #59574d !important;
	color:#6dc0cd !important;
	background:rgba(0,0,0,.85) !important;
	font-family: "Spiegel Regular",sans-serif !important;
	font-size: 8px !important;
	letter-spacing: 1px !important;
	border-radius:0px !important;
}	
}
