css:
@-moz-document domain(pleer.com) {
body {
font-size: 14px; 
color: #BFCCDF;
background-color: #01001C;
}

a {
color: #6A7C9F;
}

#hd p, #content .search p, .playlist ol li, .playlist-searches ol li, .pagination li a, .create-playlist em, .user-link-desc .btn, dt, .advanced-search, #player, .tabs li, .pagination strong, #accordion .playlist-title .playlist-title-inside, .modal h5, .modal p, .modal .close, .modal label, .breadcrumbs strong
.search-line-wrapper {
text-shadow: none;

}

.tabs .current {
border-bottom-color: #CCD9FF;
background: #3A4AA9;
cursor: default;
}

.tabs li {
list-style: none;
margin: 0 2px -1px 0;
float: left;
border: 1px solid #7890AB;
background: #001E5B;
cursor: pointer;
position: relative;
}

.tabs a {
color: #C3C7D4;
text-decoration: none;
float: left;
padding: 4px 15px;
}

li.js-tab-menu-item {
background-color: #002368;
}

.search-line-wrapper {
background: #001E5B;
}
.search .advanced-search-link {
float: right;
margin: 0;
font-size: 11px;
overflow: visible;
padding: 1px 0 0 14px;
color: #D0D0D0;
}

.pl-history ol, #accordion, #content .playlist ol, .playlist-searches ol, #warning, .lyrics, .search-line-wrapper, .advanced-search-expanded, .settings {
box-shadow: 2px 2px 5px #74849F;
}

.top-tracks .track-wrapper {
padding: 5px 5px 5px 0;
background: rgba(19, 21, 30, 0.7) url(http://pleer.com/i/top-counter-bg.png) -34px 0 no-repeat;
}

a.artist {
color: #FFF;
}

.title {
color: #BBBED5;
}

.user-link a {
color: #B7BAD9;
padding: 3px 10px;
text-decoration: none;
margin-right: -10px;
line-height: 1.4;
position: relative;
}

.sub-nav a {
float: left;
padding: 3px 0;
background: #001E5B;
}

.reg-user {
padding: 0 0 5px 0;
}

.news .news-date {
float: left;
margin-right: .5em;
background: #374268;
color: #fff;
font-size: 11px;
padding: 1px 3px;
border-radius: 2px;
}
}
