css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.gamersfirst.com") {
  #background {
    background-image:url('http://uploads.forums.gamersfirst.com/public/style_images/warrior/_bgs/g1-bg-1.jpg');
  }
  #search_wrap {
    background:#3D3D3D;
    box-shadow:none;
    border-radius:0px;   
  }
  #main_search {
    text-shadow:none;
  }
  #search_options {
    background:#333333;
    border-radius:0px;
  }
  #nav_backgrounds a {
    background:#3D3D3D;
    box-shadow:none;
    border-radius:0px;
  }
  #primary_nav {
    background:#222222;
    text-shadow:none;
    box-shadow:none;
    border-radius:0px;
  }
  #secondary_navigation {
    border-radius:0px;
  }
  #community_app_menu > li.active > a {
    background:#333333;
  }
  #community_app_menu > li > a {
    border-radius:0px;
  }
  #community_app_menu > li > a:hover {
    border-radius:0px;
    background:#333333;
  }
  #secondary_navigation {
    background:#222222;
  }
  #wrapper {
    background:#222222;
    border-radius:0px;
  }
  .sb_titlebox {
   background:#3d3d3d;
    border-radius:0px;
  }
  .maintitle {
    background:#222222;
  }
  ul.topic_buttons > li > a {
    background:#333333;
    border-radius:0px;
  }
  .post_wrap > h3 {
    background:#3d3d3d;
    border-radius:0px;
  }
  .post_block h3 {
    text-shadow: none;
  }
  tr.__topic {
    background:#222222;
  }
  #anonymous_element_77 {
    background:#222222;
  }
  #idm_category .idm_category_row, .store_categories li, #idm_categories a, .ipsList_data li, .ipsList_withminiphoto li, table.ipb_table tr td {
    background: none;
  }
  #anonymous_element_76 {
    background:#222222;
  }
  h4.statistics_head {
    background:#222222;
  }
  .ipsBox {
    background:#222222;
  }
  .ipsBox_container {
    background:#222222;
    border: 1px solid #222222;
  }
  .ipsSideMenu ul li {
    border-top: none;
    border-bottom:none;
  }
  .ipsSideMenu ul {
    border-top: none;
  }
  table.ipb_table td {
    border-bottom: none;
  }
  .maintitle {
    text-shadow:none;
  }
  .statistics_head {
    text-shadow:none;
  }
  .ftoggle {
    background:#333333;
  }
  #bar {
    position:relative;
    background:#333333;
    box-shadow:none;
  }
  .ipsLikeButton.ipsLikeButton_enabled {
    background:#222222;
  }
  ul.post_controls a {
    background:#222222;
    border-radius:0px;
  }
  .input_submit {
    border-radius:0px;
    background:#333333;
    box-shadow:none;
  }
  .input_submit.alt {
    text-shadow:none;
  }
  .input_submit:hover {
    background:#333;
    text-shadow:none;
  }
  #board_stats .value {
    box-shadow:none;
    border-radius:0px;
    text-shadow:none;
  }
  #backtotop, #bottomScroll {
    border-radius:0px;
    background:#333;
    box-shadow:none;
  }
  #backtotop:hover {
    background:#3D3D3D;
  }
  #full_compose {
    border-radius:0px;
    background:#333333;
    box-shadow:none;
  }
  .ipsUserPhoto_variable {
    max-width:135px !important;
  }
  .pagination .page a, .pagination .back a, .pagination .forward a {
    background:#3d3d3d;
    border-radius:0px;
  }
  .pagination .pages li.active {
    background:#3d3d3d;
    border-radius:0px;
    font-weight:bold;
  }
  .pagination .page a:hover, .pagination .back a:hover, .pagination  .forward a:hover {
    background:#3d3d3d;
    border-radius:0px;
    text-decoration:underline;
  }
  ul.mini_pagination li a {
    background:#3d3d3d;
    border-radius:0px;
  }
  .ipsVerticalTabbed_tabs li a {
    border-radius:0px;
  }
  .bar, .ipsSideBlock h3, .general_box h3 {
    background:#3d3d3d;
    border-radius:0px;
    text-shadow:none;
  }
  .ipsList_data li {
    border-bottom:none;
    border-top:none;
  }
  .ipsButton_secondary {
    background:#3d3d3d;
    border-radius:0px;
  }
  .ipsFilterbar li.active a, .ipsFilterbar.bar.altbar li.active a {
    background:#3d3d3d;
    box-shadow:none;
    border-radius:0px;
  }
  .content_border {
    border:none;
  }
  #announcements td, table.ipd_table td {
    border-bottom:none;
    border-top:none;
  }
  .popupWrapper {
    background:#333;
    border-radius:0px;
  }
  .stem.topright {
    background:none;
  }
  .popupInner {
    box-shadow:none;
    background:#333;
  }
  .popupInner h3 {
    border-bottom:none;
    background:none;
    text-shadow:none;
  }
  .user_control li a {
    background:none;
    border-radius:0px;
    text-shadow:none;
  }
  .userpopup dl {
    border-top:none;
    border-bottom:none;
  }
  .ipsBadge {
    text-shadow:none;
    border-radius:0px;
    background:#3d3d3d;
  }
  .reputation.positive {
    background:#3D3D3D;
  }
  .reputation.negative {
    background:#3D3D3D;
  }
  .reputation {
  border-radius:0px;
  text-shadow:none;
  }
  .ipsUserPhoto {
    box-shadow:none;
    border-radius:0px;
  }
  .ipsList_withminiphoto li {
    border-top:none;
    border-bottom:none;
  }
}
