css:
@-moz-document domain('lainchan.org'), url-prefix('https://lainchan.org')  {
    body {
       margin: 0 !important;
       padding: 0px !important;
       overflow-x:hidden!important;
       background-size: cover !important;
       background-color: #222  !important;
       background-image:
url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVQImWNwcXE5AwADNQGZSm0WOAAAAABJRU5ErkJggg==),
url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAFCAYAAACNbyblAAAAF0lEQVQImWNgwAVcXFwakGkMCcIqkQEAUyQGfeLkx5YAAAAASUVORK5CYII=), 
url(http://pile.randimg.net/0/133/33281/lain.png),
url(http://pile.randimg.net/0/133/33286/bgs.png);
       background-repeat: repeat, repeat, no-repeat, no-repeat !important;
       background-position: 0, 0, right bottom, 0!important;
       background-attachment: fixed, fixed, fixed, fixed !important;
       background-size: 1px, 5px, 30%, cover !important;
}
    html {
        margin: 0px !important;
        padding: 0px !important;
    }
    .subtitle {
        color: #aa9999 !important;
    }
    h1, h2{
        color: #cccc99 !important ;
    }
    h2 {
        background: #444 !important;
        border-bottom: 1px solid #998888 !important;
    }
    h2:not(:nth-child(1)) {
        border-top: 1px solid #998888 !important;
    }
    a:hover{
        color: #ccaa00 !important;
        text-shadow: none !important;
    }
    .boardlist a, a {
        color: #cccc99 !important;
    }
    .post.reply{
        opacity: .9;
        background: #444444 !important;
        border: 1px solid #aa9999 !important;
        color: #eee !important;
    }
    .intro .subject {
        color: #998877 !important;
    }
      [name="post"] tr *, [name="post"] [type="text"]:not([name="subject"]), #subject, #length, #ip, input[type="checkbox"], form table tr th, .mod, #search, fieldset, checkbox, .box, .ban, .board_image,  select, .pages, textarea, tbody, input, tr, th, .prettyprint{
        color: #eeeedd !important;
        background: #333 !important;
        border: 1px solid #666666 !important;
    }
    ul, li {
        color: #eeeedd !important;
    }
    .pages {
        color: #536F8C !important;
    }
    .delete, #style-select, label, footer, .unimportant, .body, .time, .fileinfo, time {
        color: #eee !important;
    }
    .name, .blotter, .quote {
        color: #ccaa00 !important;
    }
    hr {
        border: 12px solid transparent !important;
    }

    .banner{        
        color: #998877 !important;
        padding: 5px !important;
        width: 370px !important;
        background: #444444 !important;
        border: 1px solid #aa9999 !important;
    }
    .boardlist {
        text-shadow: none !important;
        background: #444444 !important;
        border-bottom: 1px solid #aa9999 !important;
        padding: 4px !important;
        margin: 0px !important;
    }
    .boardlist.bottom {
        margin-top: 41px !important;
        margin-bottom: 4px !important;
        border-top: 1px solid #aa9999 !important;
    }
    .body {
        width: 60% !important;
       
    }
    .sub {
        color: #ccaa00 !important;
            
    }
    footer {
        display: none !important;
    }
input[type="submit"]:hover {
background: #666!important;
border: 1px solid #888!important;
color: #eee!important;
}
.post.reply.highlighted, .post.reply:hover{
background: #666!important;
border: transparent 1px solid;
}
}
