css:
/* Tomorrow Theme original by Chris Kempson ( https://github.com/ChrisKempson/Tomorrow-Theme ) */
/* Theme for Firebug by zvuc ( http://xenosium.com ) */

/* i really want this to be global */
    
/* background */
.panelNode-html, 
.panelNode-stylesheet, 
.panelNode-css, 
.panelNode-html .sourceLine {
    background: #111 !important;
}

/* font set */
.panelNode-html *, 
.panelNode-stylesheet *, 
.panelNode-css * {
    font-family: Menlo,Consolas,Monaco,"Hiragino Sans GB W3", "Apple SD Gothic Neo", monospace !important;
}

.objectLink div {
    font-family: sans-serif !important;
}

.highlightOpen .nodeLabel {
    background: transparent !important;
}

.panelNode-html .highlighted .nodeLabel, 
.panelNode-html .mutated, .panelNode-html 
.mutated .nodeLabel {
    background: #333 !important;
    border-color: #555 !important;
}

.panelNode-html .sourceLine {
    border-right: dotted 1px #333332 !important;
}


/* Foreground */
.panelNode-html .nodeLabelBox .nodeAttr,
.panelNode-html .nodeText,
.panelNode-html .sourceRowText, 

.panelNode-html .nodeBox.selected .nodeLabelBox .nodeAttr,
.panelNode-html .nodeBox.selected .nodeLabel .nodeText,
.panelNode-html .nodeBox.selected .sourceRowText, 

.panelNode-html .nodeBox.nodeHidden .nodeLabelBox .nodeAttr,
.panelNode-html .nodeBox.nodeHidden .nodeText,
.panelNode-html .nodeBox.nodeHidden .sourceRowText, 

.panelNode-html .nodeBox.nodeHidden.selected .nodeLabel .nodeLabelBox .nodeAttr,
.panelNode-html .nodeBox.nodeHidden.selected .nodeLabel .nodeText,
.panelNode-html .nodeBox.nodeHidden.selected .nodeLabel .sourceRowText,

.panelNode-css .cssHead, 
.panelNode-css .cssColon, 
.panelNode-css .insertBefore, 
.panelNode-css .cssSemi,
.panelNode-css .cssPropValueUnused,
.cssSheet .cssHead, 
.cssSheet .cssColon, 
.cssSheet .insertBefore, 
.cssSheet .cssSemi,
.cssSheet .cssPropValueUnused {
    color: #eaeaea !important;
}


/* red */
.panelNode-html .nodeName, 
.panelNode-html .nodeTag,
.panelNode-html .nodeLabelBox, 
.panelNode-html .nodeCloseLabel, 
.panelNode-html .textNodeBox, 
.panelNode-html .docType,

.panelNode-html .nodeBox.selected .nodeName, 
.panelNode-html .nodeBox.selected .nodeTag,
.panelNode-html .nodeBox.selected .nodeLabelBox, 
.panelNode-html .nodeBox.selected .nodeCloseLabel, 
.panelNode-html .nodeBox.selected .textNodeBox, 
.panelNode-html .nodeBox.selected .docType,

.panelNode-html .nodeBox.nodeHidden .nodeName, 
.panelNode-html .nodeBox.nodeHidden .nodeTag,
.panelNode-html .nodeBox.nodeHidden .nodeLabelBox, 
.panelNode-html .nodeBox.nodeHidden .nodeCloseLabel, 
.panelNode-html .nodeBox.nodeHidden .textNodeBox, 
.panelNode-html .nodeBox.nodeHidden .docType,

.panelNode-html .nodeBox.nodeHidden.selected .nodeLabel .nodeName, 
.panelNode-html .nodeBox.nodeHidden.selected .nodeLabel .nodeTag,
.panelNode-html .nodeBox.nodeHidden.selected .nodeLabel .nodeLabelBox, 
.panelNode-html .nodeBox.nodeHidden.selected .nodeLabel .nodeCloseLabel, 
.panelNode-html .nodeBox.nodeHidden.selected .nodeLabel .textNodeBox, 

.panelNode-html .nodeBox.nodeHidden.selected .nodeLabel .nodeLabelBox .nodeTag,

.panelNode-css .cssSelector,
.cssSheet .cssSelector {
    color: #d54e53 !important;
}


/* orange */
.panelNode-css .cssPropValue, 
.panelNode-css .cssPropValueUsed, 
.panelNode-css .cssFontPropValue,
.cssSheet .cssPropValue, 
.cssSheet .cssPropValueUsed {
    color: #e78c45 !important;
}

/* yellow */
.panelNode-css .cssPropName,
.cssSheet .cssPropName {
    color: #e7c547 !important;
}

/* green */
.panelNode-html .nodeValue,
.panelNode-html .nodeBox.selected .nodeLabel .nodeLabelBox .nodeAttr .nodeValue,
.panelNode-html .nodeBox.nodeHidden .nodeValue,
.panelNode-html .nodeBox.nodeHidden.selected .nodeValue,
.panelNode-html .nodeBox.nodeHidden.selected .nodeLabel .nodeLabelBox .nodeAttr .nodeValue,

.cssSheet .cssFontPropValue {
    color: #b9ca4a !important;
}

/* current line */
.panelNode-html .selected * {
    background-color: #2a2a2a !important;
    
}

/* Selection */
.panelNode-html .nodeBox.selected > .nodeCloseLabel .nodeTag, 
.panelNode-html .nodeBox.selected > .nodeLabel .nodeTag {
    border-bottom: 1px dotted #eee !important;
}


/* disabled Html */
.panelNode-html .nodeBox.nodeHidden .nodeLabel .nodeLabelBox,
.panelNode-html .nodeBox.nodeHidden .nodeCloseLabelBox {
    opacity: 0.5 !important;
}


/* expand-collapse icons */
.panelNode-html .open > .nodeLabel > .twisty {
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAAJCAYAAADgkQYQAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAACRJREFUeNpi/P//PwMhwMRABKCeIhYkNjbHMaIrYhwkDgcIMACaawQUFZkD6QAAAABJRU5ErkJggg==) transparent no-repeat 50% 50% !important;
}

.panelNode-html .twisty, .panelNode-html .selected .twisty {
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAAJCAYAAADgkQYQAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAC5JREFUeNpi/P//PwMhwMRABMCm6D9ZJrHgMAHGZkRXxIikgJEqDmcky+EAAQYAqSsHE4fG1SoAAAAASUVORK5CYII=) transparent no-repeat 50% 50% !important;
}

/* CSS file link */
.panelNode-css .objectLink {
    color: #eaeaea !important;
    background: #2a2a2a !important;
    border-radius: 20px;
    padding: 2px 10px;
    margin-right: -5px;
    font-size: 0.9em;
}

/* CSS disabled style */
.panelNode-css .disabledStyle span {
    opacity: 0.5;
}

.panelNode-css .groupHeader {
    color: #eee !important;
    background: #333 !important;
    border: solid 1px #75715e !important;
    border-left: none !important;
    border-right: none !important;
    padding: 4px !important;
    margin: 10px 0 !important;
}
.panelNode-css .groupHeader * {
    color: #eee !important;
}
    

/* mutated */
.panelNode-html .nodeBox.mutated > .nodeLabel,
.panelNode-html .nodeAttr.mutated,
.panelNode-html .nodeValue.mutated,
.panelNode-html .nodeText.mutated,
.panelNode-html .nodeBox.mutated > .nodeText {
    background-color: #2a2a2a !important;
    color: #e7c547 !important;
}

.panelNode-html .nodeBox.selected.mutated > .nodeLabel,
.panelNode-html .nodeBox.selected.mutated > .nodeLabel > .nodeLabelBox,
.panelNode-html .nodeBox.selected > .nodeLabel > .nodeLabelBox > .nodeAttr.mutated > .nodeValue,
.panelNode-html .nodeBox.selected > .nodeLabel > .nodeLabelBox > .nodeAttr > .nodeValue.mutated,
.panelNode-html .nodeBox.selected > .nodeLabel > .nodeLabelBox > .nodeText.mutated {
    background-color: #2a2a2a !important;
    border-color: #e7c547 !important;
    color: #e7c547 !important;
}
