css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mozilla.org"),
domain("mozilla.net"),
domain("mozdev.org"),
domain("mozillazine.org"),
domain("seamonkey-project.org"),
domain("drumbeat.org"),
domain("mozillapopcorn.org"),
domain("mozillalabs.com"),
domain("webmaker.org")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, h3,  h5, h6, form, #footer, select, .island, .hovercard.addon:hover .icon, div.summary, div.more, #bugzilla-body, .bz_comment, .bz_alias_short_desc_container, #titles, div.postbody, div.content, .postprofile strong, .postprofile dd, .bg2, .bg1, div.highlight, select#sortby, div#contribution, .prose, #reviews .review .description, #addon h1, #addon #addon-summary, .notice p, div.lowerDiv, div.whiteBg

{
color: white !important;
background: #232323 !important;
}

section, fieldset, .secondary li a.selected, .secondary li a:hover, .menu-nav > ul > li:hover > a, #site-nav > ul > li:hover:after, .menu-nav > ul > li > ul a, .menu-nav > ul > li:hover:after, .addons li > a, #monthly > div, #go-mobile > div, .hovercard:hover, ul#side-explore li.s-users em a, ul#side-explore li.s-featured em a, div.pager-content-padding, #home-feature, #footer-wrap, #footer .six-col, div#main-content, .key-point:after, .key-point, .listing .item, .listing-header, .boxed, #article-nav, .page-toc, .page-meta section, #deki-page-alerts, #nav-toolbar, #localnav, div.catlinks, .sidebar, div#catlinks, div.item, .object-lead, .listing .item, .previews .control.prev, .previews, .previews .control.next, .notice, .wrap, .sel, .facet, div.navbar, div#breadcrumbs, ul#nav, ul#nav:after, #header h1, #header, div.download, .menu-nav ul li ul, ul#posts li, #masthead, header#intro, div#blogBg, div#endpage, div.project, div#uni-header, div#uni-footer, div.uni-download, div#uni-subheader, div.boilerplate, div#outer-wrapper, div#wrapper, div#sub-footer, h2.end-user-support, div.tabzilla-opened, div#tabzilla-promo, div#tabzilla-panel, li.hover, li.first a, #pg-wrap, #home-content-top, #home-content-nav, #colophon, .breadcrumbs, header, footer, .main-content, #tabzilla-wrapper, #quick-links, .see-also

{
background: #202020 !important;
}
  
#quick-links * {
  border-color: #202020 !important;
}

.header-search input, #daily-users, .desc, .vital, .byline, .category, .cnt, .no-results, #search-facets h2, ul li p, .field span input, .footnote, .intro, #copyright, p.note, .language-html, .secondary h2, section.primary div h2, div#main-feature h2, article p, .left-div h2, div.uni-download div p, div.promo-container p, input#q, div.snippet p, div.snippet h4, div#main-content h2, div#tabzilla-nav ul li h2, div#main-feature p, #home-content-nav section h1, p, .note

{
color: rgba(255, 255, 255, 0.75) !important;
}

#footer

{
border: black !important;
}

/*links*/

a

{
color: #595 !important;
}

/*highlight*/

textarea, pre, .header-search input, .listing .contribute, .separated-listing .contribute, .collector-note, .item .performance-note, input#q, .operator

{
background: #333333 !important;
}

  
main, #content, .wiki-main-content, #toc {
  background-color: #161616;
}
  
li a, .card-grid * {
  background-color: #161616 !important;
}
  
#main-nav ul li a, .submenu {
  background-color: #202020 !important;
}
  
div ul li a {
  background-color: #202020 !important;
}
  
.home-hacks {
  background-color: #161616 !important;
}
}
