css:
@-moz-document regexp("https?://.*\\.hipchat\\.com/chat") {
/* ===[ General ]=== */

#tooltip {
    background-color: #10171E;
    border: 1px solid #070A0D;
    -webkit-box-shadow: 0 4px 10px #19232E;
    box-shadow: 0 4px 10px #19232E; }

/* ===[ Chat ]=== */

#chats .chatBlock {
    color: white !important }

body.chat #main_chat #chats {
    border-bottom: none }

#chats .dateDivider {
    color: white !important;
    background: #2c3e50 !important }

#chats .historyLink {
    background: #10171E !important }

#chats .historyLink a {
    color: white !important }

body.chat #autocomplete {
    background: #474747 !important }

body.chat #autocomplete div.member {
    color: white !important }

.chatState.chatBlock.infoBlock {
    background: black !important }

#chats div.chatBlock {
    border-top: 1px solid #10171E }

#chats div.dateDivider {
    border-top: 1px solid #19232E }

#chats td.messageBlock {
    border-left: 1px solid #10171E !important }

#chats div.infoBlock p {
    color: #cccccc }

#chats td.messageBlock pre {
    margin: 0 0 0 10px }

/* ===[ Top Bar ]=== */

body.chat .top_bar, body.chat .top_bar_private {
    background: #22303F }

.topic_container {
    color: white }

body.chat .menu {
    color: white;
    -webkit-box-shadow: 0 4px 10px #19232E;
    box-shadow: 0 4px 10px #19232E }

body.chat #search_ui {
    background-color: #22303F;
    border: 1px solid #19232E }

/* ===[ Left Sidebar ]=== */

body.chat .logo, body.chat #tabs, body.chat li {
    background: #2c3e50 !important }

body.chat li.selected {
    background: #999999 !important }

body.chat li.selected a {
    color: black !important }

body.chat li.selected a {
    color: white !important }

body.chat #status_ui {
    background: #2c3e50 !important }

body.chat #status_chooser, body.chat #status_message_ui {
    border: 1px solid #3E5770 }

body.chat #status_chooser div {
    background-color: #3E5770 !important; color: #cccccc !important }

body.chat #status_chooser div:hover {
    background-color: #476380 !important; color: #cccccc !important }

body.chat #settings_button {
    background: none }

body.chat #status_dropdown span {
    color: #cccccc !important }

#chats div.chatDivider {
    background-color: #10171E;
    border-top: 1px solid #070A0D }

#chats p.timeBlock, #chats p.timeBlock a {
    color: #777777 }

body.chat .android {
  color: black; }

/* ===[ Right Sidebar ]=== */

#roster {
    background-color: #2c3e50 }

#roster div.member.unavailable {
    color: #999999 !important }

#roster div.member.away, #roster div.member.xa {
    color: #EA8611 !important }

#roster div.member.dnd {
    color: #EA1C10 !important }

#roster div.member, #roster div.member.available {
    color: #B2E020 !important }

#roster div.list, #roster div.member {
    background-color: #2c3e50 !important }

body.chat #sidebar {
    width: 200px;
    border: none }

#roster div.footer .guest_toggle>div, #roster div.footer .guest_url, #roster div.footer .guest_toggle, #roster div.footer .invite {
    background-color: #2c3e50; color: white;
    border-top: none }

body.chat #profile {
    color: white !important;
    background: #2c3e50 !important }

#roster div.footer .guest_toggle>div a {
    color: #cccccc }

body.chat #profile div.name {
    color: white !important }

#chats .chatBlock a, body.chat #profile a {
    color: #cccccc !important }

/* ===[ My Chats ]=== */

#chats .me {
    background: #19232E !important }

#chats .me .nameBlock {
    border-top: 1px solid #10171E !important; border-right: 1px solid #10171E !important;
    background: #10171E !important }

#chats .me .nameBlock p {
    color: #bdc3c7 !important }

/* ===[ Other's Chats ]=== */

#chats .them {
    background: #22303F !important }

#chats .them .nameBlock {
    border-top: 1px solid #10171E; border-right: 1px solid #10171E;
    background: #19232E !important }

#chats .them .nameBlock p {
    color: #ecf0f1 !important }

/* ===[ Message Input ]=== */

body.chat #main_chat .input {
    background-color: #2c3e50;
    border-top: 1px solid #22303F }

body.chat #main_chat .input .textwrapper {
    border: 1px solid #22303F }

#message_input, .textwrapper, #chats div.chat_display {
    color: white !important;
    background: #374E67 !important }

body.chat #autocomplete {
    border: 1px solid #22303F }

/* ===[ Room Chats ]=== */

body.chat #autocomplete div.selected, body.chat #autocomplete div.selected:hover {
    background-color: #2c3e50; color: white !important }

#chats .atTag {
    background: green !important;
    border: 1px solid green !important; }

#chats .atTagMe {
    background: #326CA6 !important;
    border: 1px solid #326CA6 !important; }

/* ===[ System Messages ]=== */

#chats .systemMessage .messageBlock, #chats .systemMessage .nameBlock {
    color: white !important;
    background: #22223F !important }

#chats .systemMessage .nameBlock p {
    color: white !important }

#chats div.systemMessage td.nameBlock, #chats div.systemMessage-yellow td.nameBlock {
    border-top: 1px solid #19192E; border-right: 1px solid #19192E }

#chats .systemMessage-green .messageBlock, #chats .systemMessage-green .nameBlock {
    color: white !important;
    background: green !important }

#chats .systemMessage-green .nameBlock p {
    color: white !important }

#chats .systemMessage-red .messageBlock, #chats .systemMessage-red .nameBlock {
    color: white !important;
    background: hsl(0, 50%, 30%) !important }

#chats .systemMessage-red .nameBlock p {
    color: white !important }

/* ===[ Welcome Message ]=== */

#chats div.welcomeMessage td.messageBlock {
    background-color: #22303F !important }

#chats div.welcomeMessage td.nameBlock {
    border-top: 1px solid #10171E; border-right: 1px solid #10171E;
    background: #19232E !important }

#chats td.nameBlock p {
    color: white !important }

/* ===[ Tabs ]=== */

body.chat .tabs li.selected {
    background-color: #34495e !important }

body.chat .tabs li a {
    color: #ffffff }

/* ===[ Lobby ]=== */

body.chat #lobby {
    background: #22303F !important }

body.chat #lobby .lobby_display .members {
    color: #cccccc }

body.chat #lobby .lobby_display .active {
    color: #B3B3B3 }

body.chat #lobby .lobby_display .owner {
    color: #B3B3B3 }

body.chat #lobby .lobby_display a {
    color: #cccccc !important }

body.chat #lobby .rooms_group {
    background: #2c3e50 !important;
    border: 1px solid #19232E; }

body.chat #lobby .rooms_group .lobby_display:last-child {
    border-bottom: none }

body.chat #lobby h1, body.chat #lobby h2, body.chat #lobby h2 a {
    color: white;
    text-shadow: 1px 1px 0 #000 }

body.chat .menu {
    background-color: #2c3e50;
    border: 1px solid #19232E;
    -webkit-box-shadow: 0 4px 10px #1A1A1A;
    box-shadow: 0 4px 10px #1A1A1A; }

/* ===[ Settings Dialog ]=== */

.popup {
    background-color: #34495e !important }

.popup .popup_content {
    color: white }

.popup td, .popup label {
    color: #cccccc !important }

.popup h2 {
    color: white !important;
    background: #2c3e50;
    text-shadow: 1px 1px 0 #000; }

hr {
    background-color: #2c3e50 !important }

/* ===[ Inputs ]=== */

input[type=text], input[type=password], textarea {
    background-color: #374E67; color: white;
    border: 1px solid #344960 }

input[type=submit].gray, input[type=button].gray, button.gray, .invite > .silver {
    -webkit-filter: brightness(0.5);
    filter: brightness(0.5); }

input[type=submit].silver, input[type=button].silver, button.silver {
    color: white;
    background: #2B3D50 !important;
    border: 1px solid #344960 !important; }

input[type=submit].silver:hover, input[type=button].silver:hover, button.silver:hover {
    border: 1px solid #2B3D50 }

button.silver > img, body.chat .android, .admin_gear {
    -webkit-filter: invert(100%);
    filter: invert(100%); }

/* ===[ Filters ]=== */

body.chat #status_dropdown .toggle {
    margin-top: 2px;
    -webkit-filter: invert(100%);
    filter: invert(100%); }

/* ===[ Misc ]=== */

.video_beta_label {
    color: #cccccc }

::-webkit-scrollbar {
    width: 10px; height: 12px;
    background: #19232E }

::-webkit-scrollbar-thumb {
    background: #10171E;
    -webkit-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.75); }

::-webkit-scrollbar-corner {
    background: #19232E }

/* ===[ Syntax Highlight ]=== */

@media screen {
.pln
    {color:#839496}
.str
    {color:#859900}
.kwd
    {color:#d33682}
.com
    {color:#586E75}
.typ
    {color:#859900}
.lit
    {color:#268bd2}
.pun,.opn,.clo
    {color:#dc322f}
.tag
    {color:#268bd2}
.atn
    {color:#268BD2}
.atv
    {color:#859900}
.dec,.var
    {color:#268BD2}
.fun
    {color:#dc322f}
}

}
