/* AGENT_SHEET */
@namespace url("http://www.w3.org/1999/xhtml");

@-moz-document domain("xda-developers.com"){

/* Change links to a blue color, orange sucks */
a{color:#8dd6f0!important;text-shadow:none!important}

/* Pager colors */
span.smallfont.pagenav-curpage
{
  background: #8dd6f0 !important;
}

/* Pager colors */
span.notification-count
{
  background: #ADEBFF !important;
}

/* Make the forum view wider */
div.page.page-THIS_SCRIPT
{
	margin: 0 auto !important;
	padding: 0px;
	width: 90% !important;
}
    
/* Make the usercp page wider */
div.page.page-usercp
{
	margin: 0 auto !important;
	padding: 0px;
	width: 90% !important;
}

/* Make viewing threads wider */
div.page.page-showthread
{
	margin: 0 auto !important;
	padding: 0px;
	width: 90% !important;
}

/* Remove sponsored content crap */
div.xdadepot_item
{
    display:none;
}

/* Remove Trending crap on right side */
div#rssbox
{
    display: none;
}
    
/* Remove PM panel stuff on right side */
div#sidepanel
{
    display: none;
}

/* Nav bar at the top.  Hide the stuff in it, then shrink it, leaving the breadcrumbs
/* Remove some nonsense on the left */
ul#xda-nav.headerMenu
{
    display: none;
}
/* Remove some nonsense on the right */
div#nav-right
{
    display: none;
}
    
/* Shrink it down to nothing */
div#navigation
{
    height: 1px !important;
}
 
/* Remove huge footer at bottom of page, but alsto removes Theme selection */
/* div#footer
{
