css:
@-moz-document url-prefix("http://steamcommunity.com/") {
.profile_background_overlay_content {
    opacity: .50;
}

.profile_in_game_name, .profile_in_game_header,.profile_count_link_total {
    font-weight: bold;
}

.pagecontent, #footer, #myMarketTabs, div#global_header, #BG_top > .user_info, .market_listing_row, .market_listing_table_header, .market_listing_table_message, .market_large_tab_well, .market_section_header, body {
    background: url('http://i.imgur.com/ol0416W.png') !important;
}

div#global_header .content {
    background: transparent;
}

#BG_top > .user_info {
    border-right: 1px solid transparent;
}

#myMarketTabs {
    border: 1px solid transparent;
    -webkit-box-shadow: none;
    box-shadow: none;
}

.market_large_tab_well_gradient, .market_large_tab_well .market_section_header_cap, .market_section_header .market_section_header_cap, .grouppage_content_area.blue_background, .group_content, .group_content.bottomless {
    background: none;
}

div.profile_page.has_profile_background, .profile_background_holder_content {
    background-color: transparent;
}
}
