css:
@-moz-document domain('lainchan.org'), url-prefix('https://lainchan.org')  {
    body {
       margin: 0 !important;
       margin-top: 30px !important;
       margin-bottom: 47px !important;
       padding: 0px !important;
       overflow-x:hidden!important;
       background-size: cover !important;
       background-color: #95A5A6  !important;
       background-image:
url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVQImWP4////ZgAJrwOxONcwmgAAAABJRU5ErkJggg==),
url(/*[[mascot]]*/),
url(http://pile.randimg.net/0/133/33307/lightlat.png), 
url(http://gnu.moe/000000.png);
       background-repeat: repeat, no-repeat, repeat, no-repeat !important;
       background-position: 0, right bottom, 0, 0!important;
       background-attachment: fixed, fixed, scroll, fixed !important;
       background-size: auto, 35%, auto, cover !important;
}
    .prettyprint {
        background: #fff url(http://a.pomf.se/nkwtbf.png) !important;
        padding: 4px !important;
        margin: 0px !important;
        border-radius: 10px !important;
        border: 1px solid #c0cdd1 !important;
        width: 71% !important;
        display: inline-block !important;
    }
    html {
        margin: 0px !important;
        padding: 0px !important;
    }
    .subtitle {
        color: #aa9999 !important;
    }
    h1, h2{
        color: #F62459 !important ;
    }
    h2 {
        background: #ECF0F1 !important;
        border-bottom: 1px solid #BDC3C7 !important;
    }
    h2:not(:nth-child(1)) {
        border-top: 1px solid #BDC3C7 !important;
    }
    a:hover{
        color: #34495E !important;
        text-shadow: none !important;
    }
    .boardlist a, a, .capcode{
        color: #2C3E50 !important;
    }
    .post.reply{
        border: 1px solid #c0cdd1 !important;
        background: #ecf0f1 url(http://a.pomf.se/nkwtbf.png) !important;
        border-radius: 3px !important;
        border-bottom: 3px solid #c0cdd1 !important;
    }
    .intro, .subject, .omitted {
        color: #DB0A5B !important;
    }
      [name="post"] tr *, [name="post"] [type="text"]:not([name="subject"]), #subject, #length, #ip, input[type="checkbox"], form table tr th, .mod, #search, fieldset, checkbox, .box, .ban, .board_image,  select, .pages, textarea, tbody, input, tr, th{
        color: #222 !important;
        background: #ecf0f1 url(http://a.pomf.se/nkwtbf.png) !important;
        border: 1px solid #BDC3C7 !important;
    }
    ul, li {
        color: #E74C3C !important;
    }
    .pages.bottom {
        color: #536F8C !important;
    }
        .pages.top {
        color: #536F8C !important;
        position: fixed !important;
        bottom: 0px !important;
        left: 0px !important;
        z-index: 4 !important;
        width: 100% !important;
            background: #fff !important;
    }
    .delete, #style-select, label, footer, .unimportant, .body, .time, .fileinfo, time {
        color: #111 !important;
    }
    #style-select, .styles {
        position: fixed !important;
        bottom: 0px !important;
        z-index: 4 !important;
        right: 0px !important;
    }
    .name, .blotter, .quote {
        font-family: arial, sans-serif !important;
        font-size: 12px !important;
        color: #16A085 !important;
    }
    .name, .subject {
        font-weight: 600 !important;
    }
    hr {
        border: 1px solid #eee !important;
    }

    .banner{        
        color: #C0392B !important;
        padding: 5px !important;
        width: 370px !important;
        border-radius: 5px !important;
        background: #ECF0F1 !important;
        border: 1px solid #BDC3C7 !important;
    }
    .boardlist, th {
        text-shadow: none !important;
        background: #ECF0F1 !important;
        border-bottom: 1px solid #BDC3C7 !important;
        padding: 4px !important;
        margin: 0px !important;
    }
    hr {
        margin-top: 1em !important;
        border: none !important;
    }

    .sub {
        color: #999 !important;
            
    }
    footer {
        display: none !important;
    }
    tbody, th, #upload_file, label, input {
        color: #674172 !important;
    }
   input[type="submit"]:hover, .post.reply.highlighted, .post.reply:hover{
        background: #fff url(http://a.pomf.se/nkwtbf.png) !important;
        border: 1px solid #aaa !important;
        border-bottom: 3px solid #aaa !important;
}
}
