css:
@-moz-document domain('lainchan.org'), url-prefix('https://lainchan.org')  {
    body {
       margin: 0 !important;
       margin-top: 30px !important;
       margin-bottom: 47px !important;
    }
    header {
       margin: 3em 0px !important;
    }
    .boardlist.bottom {
        position: relative !important;
        margin-top: 71px !important;
        margin-bottom: 40px !important;
        z-index: 1 !important;
    }

    .boardlist {
        position: fixed !important;
        width: 100% !important;
        margin: 0 !important;
        top: 0 !important;    
        z-index: 6 !important;        
    }
    .pages.bottom {
        position: fixed !important;
        z-index: 99 !important;
        margin-top: -33px !important;
        border-left: none;
        border-bottom: none;
    }
        .pages.top{
        color: #536F8C !important;
        border-top: 1px solid #333 !important;
        border-right: 1px solid #333 !important;
        bottom: 0px !important;
        left: 0px !important;
        z-index: 99999 !important;
        position: fixed !important;
            
        }
    #style-select, .styles {
        position: fixed !important;
        bottom: 0px !important;
        z-index: 4 !important;
        right: 0px !important;
    }

}
