css:
/* i really want this to be global */
body{
  background-image:url('http://deepofman.com/wp-content/uploads/2014/02/beyaz-abstract-bg.jpg');
color:black;
background-color:rgba(255,255,255,1);
background-attachment:fixed;
background-size:100% 100%;
font-family:AvenirNext-Regular,GillSans-Light, HelveticaNeue-Light, Arial!important;
}
input[type=range]{
outline:none;
-webkit-appearance:none;
background:rgba(0,0,0,1);
height:1px;
}
input[type=range]::-webkit-slider-thumb{
-webkit-appearance:none;
background:rgba(255,255,255,1);
height:16px;
width:16px;
border-radius:100%;
border:1px solid black;
}
  #content{
   background:rgba(255,255,255,0.2); 
  }
  #mw-head{
   background:rgba(255,255,255,0.2); 
  }
input[type=text],input[type=search],input[type=password]{
outline:none;
color:black;
-webkit-appearance:none;
font-family:AvenirNext-Regular,GillSans-Light, HelveticaNeue-Light, Arial;
background:rgba(0,0,0,0);
border:2px solid rgba(30,144,255,0.3);
height:20px;
transition:background 0.3s, border 0.3s, color 0.3s;
-webkit-transition:background 0.3s, border 0.3s, color 0.3s;
-moz-transition:background 0.3s, border 0.3s, color 0.3s;
-o-transition:background 0.3s, border 0.3s, color 0.3s;
}
input[type=text]:hover,input[type=text]:focus,input[type=search]:hover,input[type=search]:focus{
-webkit-appearance:none;
background:rgba(255,255,255,0.3);
border:2px solid dodgerblue;
}
button,input[type=submit],input[type=button]{
outline:none;
font-size:12px;
font-family:AvenirNext-Regular,GillSans-Light, HelveticaNeue-Light, Arial;
-webkit-appearance:none;
color:dodgerblue;
border:1px solid white;
background:rgba(0,0,0,0);
border-radius:0%;
transition:background 0.3s, border 0.3s, color 0.3s;
-webkit-transition:background 0.3s, border 0.3s, color 0.3s;
-moz-transition:background 0.3s, border 0.3s, color 0.3s;
-o-transition:background 0.3s, border 0.3s, color 0.3s;
}
button:hover,input[type=submit]:hover,input[type=button]:hover{
background:rgba(255,255,255,0.5);
color:dodgerblue;
}
button:active,input[type=submit]:active,input[type=button]:active{
color:black;
color:dodgerblue;
background:rgba(255,255,255,0.9);
}
a{
text-decoration:none;
outline:none;
font-size:18px;
font-family:AvenirNext-Regular,GillSans-Light, HelveticaNeue-Light, Arial;
text-decoration:none;
color:dodgerblue!important;
transition:text-shadow 0.3s;
-webkit-transition:text-shadow 0.3s;
-moz-transition:text-shadow 0.3s;
-o-transition:text-shadow 0.3s;
}
        h1{
          font-family:AvenirNext-Regular,GillSans-Light, HelveticaNeue-Light, Arial!important;
        }
        div{
          font-family:AvenirNext-Regular,GillSans-Light, HelveticaNeue-Light, Arial!important;
        }

hr{
-webkit-appearance:none;
border:1px solid rgba(255,255,255,0.75);
}
fieldset{
background:rgba(255,255,255,0.3);
border:1px solid rgba(255,255,255,0.3);
}
select{
padding:3px;
outline:none;
font-size:12px;
font-family:AvenirNext-Regular,GillSans-Light, HelveticaNeue-Light, Arial;
-webkit-appearance:none;
color:white;
border:1px solid rgba(255,255,255,0.75);
border-bottom:3px solid rgba(255,255,255,0.75);
background:rgba(0,0,0,0.2);
border-radius:0%;
transition:background 0.3s, border 0.3s, color 0.3s;
-webkit-transition:background 0.3s, border 0.3s, color 0.3s;
-moz-transition:background 0.3s, border 0.3s, color 0.3s;
-o-transition:background 0.3s, border 0.3s, color 0.3s;
}
table{
font-family:AvenirNext-UltraLight;
background:rgba(255,255,255,0.2)!important;
border:1px solid rgba(255,255,255,0.75);
}
tbody{
font-family:AvenirNext-UltraLight;
background:rgba(255,255,255,0.2);
border:1px solid rgba(255,255,255,0.75);
}
td{
border:1px solid rgba(255,255,255,0.75);
background:rgba(0,0,0,0)!important;
}
th{
border:1px solid rgba(255,255,255,0.75);
background:rgba(0,0,0,0)!important;
font-weight: regular;
}
ul{
border:1px solid rgba(255,255,255,0.75);
background:rgba(0,0,0,0)!important;
}
tr{
border:1px solid rgba(255,255,255,0.75);
background:rgba(0,0,0,0);
}
textarea{
outline:none;
background:rgba(255,255,255,0.2);
border:1px solid rgba(255,255,255,0.75);
}
#hdtb_msb .hdtb_mitem.hdtb_msel, #hdtb_msb .hdtb_mitem.hdtb_msel_pre,#hdtb_msb .hdtb_mitem.hdtb_msel, #hdtb_msb .hdtb_mitem.hdtb_msel_pre{
          text-decoration:none;
outline:none;
font-size:18px;
font-family:AvenirNext-Regular,GillSans-Light, HelveticaNeue-Light, Arial;
text-decoration:none;
color:dodgerblue!important;
transition:text-shadow 0.3s;
-webkit-transition:text-shadow 0.3s;
-moz-transition:text-shadow 0.3s;
-o-transition:text-shadow 0.3s;
font-weight:regular!important;
          }
          span{
font-family:AvenirNext-Regular,GillSans-Light, HelveticaNeue-Light, Arial;
          }
#page {
    background: rgba(255,255,255,0.5);
}
#rightcolumn.rightad, #rightshare {
    background: rgba(255,255,255,0.5);
}
input[type=radio] {
    -webkit-appearance:none;
    width:16px;
    height:16px;
    border-radius:100%;
    background:rgba(255,255,255,0.75);
}
input[type=radio]:checked {
    background:-webkit-radial-gradient(dodgerblue 30%, rgba(255,255,255,0.75) 50%);
}
input[type=checkbox] {
    -webkit-appearance:none;
    width:16px;
    height:16px;
    border-radius:0%!important;
    background: rgba(255,255,255,1);
}
input[type=checkbox]:checked {
    border:2px solid rgba(255,255,255,1);
    background:rgba(30,144,255,1);
}
                  em {
font-weight: normal;
font-style: normal;
}
                  b{
                    font-weight: normal;
font-style: normal;
                  }
                                    strong{
                    font-weight: normal;
font-style: normal;
                    font-family:AvenirNext-Regular,GillSans-Light, HelveticaNeue-Light, Arial;
                  }
                  div,p{
                    text-decoration:none!important;
                    font-weight:normal!important;
font-family:AvenirNext-Regular,GillSans-Light, HelveticaNeue-Light, Arial!important;
                  }
