css:
@-moz-document domain("chip.de") {
.newsticker-wrapper .newsticker-header {
	background-color: #1f6ab7; 
    height: 30px;
    float: none;
   	margin-bottom: 0;
}

.newsticker-header .newsticker-headline-wrapper a h1 {
	color: white !important; 
    margin: 0;
	padding: 6px;
}
      
.newsticker-header .newsticker-rss-wrapper {
    margin: 0 !important;
	padding: 6px !important;   
}
          
.newsticker-header .newsticker-rss-wrapper img {
    display: none;
        
}
              
.newsticker-header .newsticker-rss-wrapper a {
    display: block;
    width:            14px;
	height:           14px;
	background-image: url('data:image/png;base64,');
	background-repeat: no-repeat;
        
}
  
.newsticker-wrapper .newsticker-entries article {
  position: relative;
  margin-bottom: 0 !important;
  padding: 5px 3px;
}

.newsticker-wrapper .newsticker-entries article:nth-child(even) {
  background-color: rgba(200, 200, 200, 0.1);
}
  
.newsticker-wrapper .newsticker-entries article time {
  display: none;
}
    
.newsticker-wrapper .newsticker-entries article a.newsticker-item-link {
  position: relative;
  display: block;
  width: 100%;
  height: 100%;
  
  font-size: 13px;
  color: #444;
}
              
.newsticker-wrapper .newsticker-entries article:hover {

	background-color: rgba(31, 106, 183, 0.1);
                      
}

.newsticker-wrapper .newsticker-entries article a.newsticker-item-link b {
   font-size: 14px;
}
}
