css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://download.autodesk.com/global/docs"), 
regexp('http://download.autodesk.com/global/docs/maya.*/.*')
{
  .iframeContent, body, td, .command #banner, #help, #resources, #reference, 
  #new, p.credit, #content{
    background-color: #444;
    color: #aaa;
  }
  a, a.treeitem, a.index_link {
    color: #3af;
  }
  a:visited{
    color: #5af;
  }
  a.button{
    background-color: #aaa;
    color: #6bf;
    text-shadow: 2px 2px 2px rgba(0, 0, 0, .3);
    box-shadow: 0 10px 15px 5px rgba(0, 0, 0, .5) inset, 2px 2px 2px 2px rgba(0, 0, 0, .12);;
  }
  p.sep{
    background-color:#333;
  }
  p#synopsis{
    background-color: #444;
    color: #ccc;
    border: 1px solid #222;
    padding: 2px;
    box-shadow: 0 0 25px 15px rgba(0, 0, 0, .25) inset;
    padding: 5px;
    line-height: 1.1em;
  }
  tr.book-title__row{
    display:none;
  }
  td.tab_inactive{
    background-image:none;
    background-color: #555;
    border-right: 1px solid #444;
    box-shadow: 0 0 10px 10px #333 inset;
  }
  td.tab_active{
    background-image:none;
    background-color: #58f;
    border-right: 1px solid #444;
    box-shadow: 0 5px 20px 10px #115877 inset;
  }
  input[name="ssfield"], input[type="text"]{
    background-color: #444;
    color: #ccc;
    border: 1px solid #222;
    padding: 2px;
    box-shadow: 0 0 8px 6px rgba(0, 0, 0, .25) inset;
  }
  body.command table{
    background-color:#282828;
    border:none;
  }
  body.command table table{
    border-collapse: collapse;
  }
  body.command th {
    background-color: #444;
    border-right: 1px solid #333;
    box-shadow: 0 0 10px 10px #222 inset;
    padding: 2px;
  }
  body.command tr[bgcolor="#DDDDDD"] td{
    background-color: #444;
    border-right: 1px solid #333;
    box-shadow: 0 0 10px 10px #222 inset;
    padding: 2px;
  }
  body.command tr, body.command td {
    background-color: #343434;
    border:none;
  }
  body.command tr[bgcolor="#EEEEEE"], body.command tr[bgcolor="#EEEEEE"] td {
    background-color: #303030;
    border:none;
  }
  .ui-beehive-search-panel > .results, .ui-beehive-search-panel > .results div {
    background-color: #333;
  }
  #resultsPanel.ui-scroll-group .scrollbar {
    background-image:none;
    background-color: #555;
    box-shadow: 0 0 7px 0px rgba(0, 0, 0, .5) inset;
  }
  #categorySelector.ui-corner-all, #categorySelector .ui-corner-all {
    border: 1px solid #222;
    border-radius: 4px;
  }
  #categorySelector.ui-dropdown .ui-dropdown-header{
    background-image:none;
    background-color: #444;
    box-shadow: 0 0 8px 3px rgba(0, 0, 0, .25) inset;
    text-shadow:none;
  }
  #categorySelector.ui-dropdown .ui-dropdown-header #header {
    background-image:url("http://beehive.autodesk.com/client/remorav2/beehiveSearch/styles/scrollbar/scrollbar_arrow_down_hl.png");
  }
  #categorySelector li div, #categorySelector #list, #categorySelector li.selected{
    background-color: #555;
  }
  #categorySelector .ui-dropdown-list li.ui-list-item.selected,
  #categorySelector .ui-dropdown-list li.ui-list-item.selected div{
    background-color: #358;
    color:#3af;
  }
}
