ul#main-nav li a {background: #1a1a1a !important;}
a {color: #ccffff !important;}
textarea, pre {background: #1a1a1a !important;}

h1.site-logo a {
  background: url(data:image/png;base64,);}

#widgetPopularStories .widget-tabs .widget-tabs-nav li.ui-tabs-selected a {background: #333 !important;}
