css:
@-moz-document domain("www.fimfiction.net") {
body, a {
font-family: Sans-serif !important;
}

.comment_content_container .textbox_container textarea {
font-family: Sans-serif !important;
}

div.thread_list table td a.thread-title {
font-family: Sans-serif;
font-size: 1.2em;
}

.user-card .user-name {
text-align: left;
color: #000;
font-weight: bold;
font-size: 1.2em;
padding-bottom: 10px;
padding-left: 5px;
}

.story-card h2 {
  font-size:0.9em;
  background-color: #eee;
  padding-top:5px;
  padding-left:7px;
  border-top:1px solid #ccc;
  border-bottom:1px solid #ccc !important;
}

.info-internal .group-name {
  font-size:2em;
}
}
