css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("drupal.org") {

body {
  background: #e1e7eb !important;
}

div#contentwrapper {
  background: #fff none !important;
  border-left: 1px solid #a6c8dd !important;
  border-right: 1px solid #a6c8dd !important;
  margin-left: auto !important;
  margin-right: auto !important;
  width: 98% !important;
}

#content #squeeze {
  padding-left:25px !important;
  padding-right:25px !important;
  padding-top:0px !important;
}
img#logo {
  border-width:0 !important;
  left:5% !important;
  position:absolute !important;
  top:0 !important;
  z-index:3 !important;
}
div#tabfloat {
  left:0 !important;
  position:absolute !important;
  top:44px !important;
  width:90% !important;
  z-index:2 !important;
}
div#search {
  background:transparent none no-repeat scroll 0 0 !important;
  height:29px !important;
  position:absolute !important;
  right:5% !important;
  top:10px !important;
  white-space:nowrap !important;
  z-index:2 !important;
}
#content .node {
  margin-bottom: 3em !important;
}
#content .node h2.node-title {
  font-weight: bold !important;
  font-size: 1.5em !important;
  border-bottom: 0 !important;
  border-top: 1px solid #8CA6B6 !important;
  padding-top: 1em !important;
}
/* hide the meta bits form the 'quick links' block in the right column */
div.panel-col-2 div.terms,
div.panel-col-2 div.visibility,
div.panel-col-2 div.links {
  display: none !important;
}
div#sidebar-right {
  margin-top: 2em !important;
  background: #fff !important;
}
div#sidebar-right div.block {
  background: #dfedf7 !important;
  font-size: 85% !important;
}
/* hide the 'recently joined' user list */
div#block-og-2 {
  display: none !important;
}
div.visibility,
div.node div.groups {
  display: none;
}
span.marker {
  font-size: 50% !important;
}
tr.odd, tr.dark {

}
tr.even {

 border-bottom:1px solid #eee !important;
 padding:0.1em 0.6em !important;
}

/* kill the round corners */
.w1, .w2, .w3, .w4 {
  background: transparent none !important;
}

#content a, .sidebar a, #project-overview .links a {
  font-weight:normal !important;
}

.sidebar .item-list ul li {
  list-style:none !important;
  margin:0 0 0.25em 1.25em !important;
  padding:0;
}


#mission {
  display: none;
}

#content .sticky .w4 {
  padding: 0 1.2em !important;
}


.views-field-project-issue-queue,
.views-field-title,
.views-field-sid,
.views-field-priority,
.views-field-category,
.views-field-version,
.views-field-comment-count,
.views-field-name {
  background-color: #fff;
}



}
