css:
@-moz-document url("http://www.fimfiction.net/"), url("https://www.fimfiction.net/") {
header, .nav_bar .light
{
  width: auto !important;
  max-width: 1260px !important;
}

.body_container
{
  background-image: url("https://dl.dropboxusercontent.com/u/23276645/userstyles/background___golden_oak_library_by_hawk9mm-d7wivxg_nosky.svg") !important;
  background-position: bottom 250px center !important;
  background-repeat: no-repeat !important;
  background-size: 100% auto !important;
}

.content_background .inner
{
  max-width: 1260px !important;
  width: auto !important;
}

.front_page
{
  position: relative !important;
  min-height: 2260px !important;
}

.front_page .column_latest_stories
{
  margin: 0px !important;
}

.column_latest_stories>.inner_padding::before
{
  content: "\f0a3";
  position: absolute !important;
  top: 4px !important;
  left: 4px !important;
  font-family: "FontAwesome";
  display: inline-block;
  margin: -1px;
  border: 1px solid #4f4d4b;
  margin-right: 10px;
  background: #5a5856;
  color: #fff;
  width: 36px;
  line-height: 41px !important;
  text-align: center;
  border-top-left-radius: 4px;
  border-bottom-left-radius: 4px;
  z-index: 2 !important;
}

.column_latest_stories>.inner_padding::after
{
  position: absolute !important;
  top: 4px !important;
  left: 41px !important;
  width: 257px !important;
  content: "Featured Stories" !important;
  margin: -1px !important;
  margin-bottom: 0px !important;
  padding-left: 10px !important;
  font-size: 17px !important;
  font-weight: normal !important;
  border: 1px solid #5a5856 !important;
  color: #fff !important;
  box-shadow: 0px 1px 0px #878481 inset !important;
  text-shadow: -1px -1px rgba(0,0,0,0.2) !important;
  background: linear-gradient(to bottom, #767371 0%, #6b6866 100%) !important;
  line-height: 41px !important;
  font-family: "Open Sans",Arial,Sans-serif !important;
  border-radius: 0 3px 3px 0 !important;
  z-index: 1 !important;
}

.featured_box
{
  height: auto !important;
  width: 1260px !important;
  box-sizing: border-box !important;
  border-top-width: 49px !important;
  background-image: url("https://dl.dropboxusercontent.com/u/23276645/userstyles/white_wave.png") !important;
}
.featured_box .left
{
  display: none !important;
}

.featured_box .right-container
{
  margin: 0px !important;
  height: auto !important;
}

.featured_box .right
{
  top: 0px !important;
  height: auto !important;
  margin-left: 4px !important;
}

.featured_box .right.changing
{
  opacity: 1 !important;
}

.featured_box .right-container::before, .featured_box .right-container::after
{
  display: none !important;
}

.featured_box .featured_story .title
{
  min-height: 1.5em !important;
  max-height: 1.5em !important;
  padding: 0 0 0.25em 0.5em !important;
  white-space: nowrap !important;
  overflow: hidden !important;
  text-overflow: ellipsis !important;
  transition: max-height 1.5s linear 1s !important;
}
.featured_box .featured_story:hover .title
{
  max-height: 150px !important;
  white-space: normal !important;
}

.featured_box .featured_story .info
{
  min-height: 56px !important;
}

.featured_box .right
{
  height: 1234px !important;
}

.featured_box,
.featured_box .right,
.featured_box .right-container
{
  overflow: visible !important;
}

#featured_story_1  { top: 0;     left: 0;     z-index: 0; }
#featured_story_2  { top: 304px; left: 0;     z-index: 0; }
#featured_story_3  { top: 304px; left: 417px; z-index: 0; }
#featured_story_4  { top: 304px; left: 834px; z-index: 0; }
#featured_story_5  { top: 608px; left: 0;     z-index: 0; }
#featured_story_6  { top: 608px; left: 417px; z-index: 0; }
#featured_story_7  { top: 608px; left: 834px; z-index: 0; }
#featured_story_8  { top: 916px; left: 0;     z-index: 0; }
#featured_story_9  { top: 916px; left: 417px; z-index: 0; }
#featured_story_10 { top: 916px; left: 834px; z-index: 0; }

#featured_story_1
{
  display: block !important;
  height: auto !important;
  width: 1244px !important;
  margin: 2px !important;
  padding: 10px !important;
  background-color: white !important;
  border: 1px solid #CCC !important;
  border-radius: 3px !important;
  box-shadow: 2px 4px 5px #CCC;
}

.featured_box .featured_story:not(#featured_story_1)
{
  display: inline-block !important;
  width: 410px !important;
  padding: 10px 4px 4px 4px !important;
  margin: 10px 2px 4px 2px !important;
  vertical-align: top !important;
}

.featured_box .featured_story
{
  position: absolute !important;
  height: auto !important;
  background-color: white !important;
  border: 1px solid #CCC !important;
  border-bottom-color: white !important;
  border-radius: 3px !important;
  box-shadow: 2px 4px 5px #CCC;
  transition: box-shadow 0.5s 1s;
  opacity: 1 !important;
}

.featured_story:hover
{
  box-shadow: 0px 0px 8px 4px gold !important;
  z-index: 1 !important;
}

.featured_box .featured_story::after
{
  opacity: 0 !important;
  pointer-events: none !important;
  position: absolute !important;
  bottom: -41px !important;
  height: 0px !important;
  width: 0px !important;
  border: 20px solid transparent !important;
  border-top-color: #CCC !important;
  z-index: 1 !important;
  content: " " !important;
  transition: border-top-color 0.5s 1s, opacity 0.25s ease-out;
}

#featured_story_1::after
{
  left: 610px !important;
}

.featured_box .featured_story:not(#featured_story_1)::after
{
  left: 197px !important;
}

.featured_box .featured_story:hover:after
{
  opacity: 1 !important;
  border-top-color: gold !important;
}

.featured_box .featured_story .description
{
  position: relative;
  border-bottom: 2px dashed transparent;
  transition: max-height 1.5s ease 1s;
  height: auto !important;
  min-height: 176px !important;
  max-height: 176px !important;
  overflow: hidden !important;
}

.featured_box .featured_story:hover .description
{
  max-height: 750px !important;
}

.featured_box .featured_story .description::before
{
  content: "END OF PREVIEW";
  display: inline-block;
  position: absolute;
  top: 731px;
  left: 0px;
  width: 405px;
  padding: 6px 4px 2px 4px;
  opacity: 0;
  color: red;
  text-align: center;
  font-size: 12px;
  line-height: 12px;
  font-weight: bold;
  font-family: "Open Sans Condensed",Arial,Sans-serif;
  border-top: 1px solid red;
  background-color: white;
  transition: opacity 0.25s ease 1s;
}

.featured_box .featured_story .description:hover::before
{
  opacity: 1;
}

.featured_box .featured_story .description::after
{
  position: relative;
  top: 4px;
  display: block;
  clear: both;
  padding: 6px 4px 2px 4px;
  opacity: 0;
  content: "END OF DESCRIPTION";
  color: #CCC;
  text-align: center;
  font-size: 12px;
  line-height: 12px;
  font-weight: bold;
  font-family: "Open Sans Condensed",Arial,Sans-serif;
  border-top: 1px solid #CCC;
  background-color: white;
  transition: opacity 0.25s ease 1s;
}

.featured_box .featured_story .description:hover::after
{
  opacity: 1;
}

#featured_story_4,
#featured_story_8,
#featured_story_10
{
  margin-right: 0px !important;
}

.front_page_right_column
{
  pointer-events: none !important;
  position: absolute !important;
  z-index: 1 !important;
  top: 0px !important;
  right: 0px !important;
  width: 1300px !important;
  padding: 0px !important;
}

.front_page_right_column .sidebar-shadow
{
  display: none !important;
}

.front_page .popular-stories-container::after
{
  display: none !important;
}

#popular_stories
{
  pointer-events: none !important;
  width: 1260px !important;
  margin: 0px !important;
}

#popular_stories h1
{
  pointer-events: visible !important;
  left: 312px !important;
  width: 211px !important;
  display: inline-block !important;
  position: absolute !important;
  top: 4px !important;
  padding-right: 10px !important;
  z-index: 1 !important;
  transition: transform 0.25s ease-out;
}

#popular_stories:hover h1
{
  transform: translateY(10px) !important;
}

#popular_stories .story-card-list
{
  pointer-events: visible !important;
  visibility: hidden !important;
  opacity: 0 !important;
  height: auto !important;
  margin: 0 !important;
  padding: 12px 0 10px 0 !important;
  background-color: gold !important;
  background-image: url("https://dl.dropboxusercontent.com/u/23276645/userstyles/transparent_white_wave.png") !important;
  padding-bottom: 10px !important;
  border: 1px solid #bebab4;
  border-top-width: 49px !important;
  border-left-color: #d6d1cb;
  border-top-color: #d6d1cb;
  border-radius: 5px !important;
  /* box-shadow: 0px 0px 16px 8px gold !important; */
  transition: opacity 0.35s ease-in, visibility 0s linear 0.35s !important; 
}

#popular_stories:hover .story-card-list
{
  transition-delay: 0s !important;
  visibility: visible !important;
  opacity: 1 !important;
}

#popular_stories .story-card-list li
{
  float: left !important;
  padding: 6px !important;
  display: inline-block !important;
  vertical-align: top !important;
  width: 314px !important;
}

#popular_stories .story-card-list .story-card
{
  box-shadow: 2px 4px 5px rgba(0,0,0,0.2);
}


#popular_stories>a
{
  display: none !important;
}

#latest_stories,
#new_stories
{
  pointer-events: none !important;
  position: absolute !important;
  top: 0 !important;
  left: 0 !important;
}

#latest_stories h1,
#new_stories h1
{
  pointer-events: visible !important;
  position: absolute !important;
  top: 4px !important;
  width: 211px !important;
  padding-right: 10px !important;
  z-index: 1 !important;
  transition: transform 0.25s ease-out;
}

#latest_stories:hover h1,
#new_stories:hover h1
{
  transform: translateY(10px) !important;
}

#latest_stories h1
{
  left: 525px !important;
}
#new_stories h1
{
  left: 738px !important;
}

#latest_stories .story-card-list,
#new_stories .story-card-list
{
  pointer-events: visible !important;
  visibility: hidden !important;
  opacity: 0 !important;
  background-color: white !important;
  background-image: url("https://dl.dropboxusercontent.com/u/23276645/userstyles/transparent_white_wave.png") !important;
  padding: 12px 0 10px 0 !important;
  margin: 0 !important;
  border: 1px solid #bebab4 !important;
  border-top-width: 49px !important;
  border-left-color: #d6d1cb !important;
  border-top-color: #d6d1cb !important;
  border-radius: 5px !important;
  transition: opacity 0.35s ease-in, visibility 0s linear 0.35s !important;
  box-sizing: border-box;
}

#latest_stories .story-card-list
{
  background-color: green !important;
  /* box-shadow: 0px 0px 16px 8px green !important; */
}

#new_stories .story-card-list
{
  background-color: #007FFF !important;
  /* box-shadow: 0px 0px 16px 8px #007fff !important; */
}

#latest_stories:hover .story-card-list,
#new_stories:hover .story-card-list
{
  transition-delay: 0s !important;
  visibility: visible !important;
  opacity: 1 !important;
}

#latest_stories .story-card-list li,
#new_stories .story-card-list li
{
  vertical-align: top !important;
}

#latest_stories .story-card,
#new_stories .story-card
{
  box-shadow: 2px 4px 5px rgba(0,0,0,0.2);
}

#latest_stories>a,
#new_stories>a
{
  display: none !important;
}

#latest_stories, #new_stories
{
  margin: 0px !important;
}

.new_stories_outer,
.latest_stories_outer
{
  width: 1260px !important;
}

.new_stories_outer .story-card-list li,
.latest_stories_outer .story-card-list li
{
  display: inline-block !important;
  width: 314px !important;
  padding: 6px !important;
}

.front_page_right_column
{
  width: 1260px !important;
}

.front_page_right_column .content_box
{
  pointer-events: none !important;
  position: absolute !important;
  top: 4px !important;
  right: 4px !important;
  width: 305px !important;
  margin: 0px !important;
  background: none !important;
  border: none !important;
}

.front_page_right_column .content_box h1
{
  pointer-events: visible !important;
  border-radius: 4px !important;
  z-index: 1;
  transition: transform 0.25s ease-out;
}

.front_page_right_column .content_box:hover h1
{
  transform: translateY(10px) !important;
}

.front_page_right_column .content_box .main
{
  pointer-events: visible !important;
  visibility: hidden !important;
  opacity: 0 !important;
  padding: 11px 0 0 0 !important;
  background-color: white !important;
  border: 1px solid #bebab4 !important;
  border-left-color: #d6d1cb !important;
  border-top-color: #d6d1cb !important;
  border-radius: 5px !important;
  /* box-shadow: 0px 0px 16px 8px white !important; */
  transition: opacity 0.35s ease-in, visibility 0s linear 0.35s !important; 
}

.front_page_right_column .content_box:hover .main
{
  transition-delay: 0s !important;
  visibility: visible !important;
  opacity: 1 !important;
}

.front_page_right_column .content_box .main
{
  background: red !important;
  background-image: url("https://dl.dropboxusercontent.com/u/23276645/userstyles/transparent_white_wave.png") !important;
}

.front_page_right_column .content_box .main .list li
{
  border: 1px solid #bebab4;
  border-left-color: #d6d1cb;
  border-top-color: #d6d1cb;
  border-radius: 5px !important;
  background-color: white !important;
  margin: 10px 6px !important;
}

.front_page_right_column .content_box .styled_button
{
  display: none !important;
}
}
