css:
@-moz-document domain(forum.userstyles.org){

#Head {

background:#FFEC8B!important;
  border-radius:6px!important;
  margin-top:8px!important;
  margin-left: auto!important;
  margin-right: auto!important;  
  padding:9px!important;
  width:960px!important
  
}

.SiteTitle a{
  color:#8A56D2!important;
  text-decoration:underline!important;
  font-family: Trebuchet MS!important;
  font-size:26px!important;
  margin-top:-8px!important;
  margin-bottom:3px!important
}

.SiteMenu li a {
  color:#222!important;
  font-family: Trebuchet MS!important;
  font-size:12px!important
  
}
/*[[Heart]]*/
}
@-moz-document domain(freestyler.ws), domain(css.firefoxfan.com){

#content{
  visibility:hidden !important;
}

#content:before{
  content:"Visit Userstyles.Org for all your userstyle needs"!important;
  visibility:visible !important;
  margin:5%!important;
 
  color:#B878B3!important;
  font-family: "Open Sans",Ariel,san-serif!important;;  
  font-size:52px!important;
  text-shadow: -2px 0 2px #24409D, 0 1px 1px black, 1px 0 1px #24409D, 0 -1px 1px #24409D!important;
}

#content div.w {
  display:none!important;
}


#content:after{
  content:"A leech has no honor."!important;
  visibility:visible !important;
  margin-left:5%!important;
  font-size:15px!important;
}
}

style_setting_options.value:
#Head:before,#Head:after { 
  position: absolute; content: "";
  left: 30px;
  top: 12px;
  width: 20px;
  height: 33px; 
  background: #8A56D2; 
  border-radius: 50px 50px 0 0 ; 
  transform: rotate(-45deg);
  transform-origin: 0 100%;
}

#Head:after {
  left: 10px;
  transform: rotate(45deg);
  transform-origin :100% 100%;}
